import React, { useState, useEffect } from 'react';
import {
    Box,
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Chip,
    IconButton,
    Alert,
    CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

const priorities = [
    { value: 'high', label: 'High', color: '#DC143C' },
    { value: 'medium', label: 'Medium', color: '#FFA500' },
    { value: 'low', label: 'Low', color: '#4CAF50' }
];

function TaskScheduler() {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        subject: '',
        priority: 'medium',
        estimatedTime: ''
    });

    // Fetch tasks on component mount
    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            console.log('Using token:', token); // Debug log

            const response = await axios.get(`${API_URL}/tasks`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            console.log('Response:', response.data); // Debug log
            setTasks(response.data);
        } catch (err) {
            console.error('Full error:', err); // More detailed error logging
            setError(err.response?.data?.error || 'Failed to fetch tasks');
        } finally {
            setLoading(false);
        }
    };    

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setLoading(true);

        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_URL}/tasks`,
                formData,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            setTasks([...tasks, response.data]);
            setSuccess('Task added successfully!');
            setFormData({
                title: '',
                description: '',
                subject: '',
                priority: 'medium',
                estimatedTime: ''
            });
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add task');
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (taskId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`${API_URL}/tasks/${taskId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setTasks(tasks.filter(task => task.id !== taskId));
            setSuccess('Task deleted successfully!');
        } catch (err) {
            setError('Failed to delete task');
        }
    };

    const textFieldStyle = {
        '& .MuiOutlinedInput-root': {
            backgroundColor: 'rgba(255, 255, 255, 0.03)',
            '& fieldset': {
                borderColor: 'rgba(255, 255, 255, 0.1)',
            },
            '&:hover fieldset': {
                borderColor: 'rgba(255, 255, 255, 0.2)',
            },
            '&.Mui-focused fieldset': {
                borderColor: '#DC143C',
            }
        },
        '& .MuiInputLabel-root': {
            color: 'rgba(255, 255, 255, 0.7)',
        },
        '& .MuiInputBase-input': {
            color: '#fff',
        }
    };

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4,
                px: 3
            }}
        >
            <Container maxWidth="lg"> {/* Changed from xl to lg for better centering */}
                <Grid container spacing={4} justifyContent="center"> {/* Added justifyContent="center" */}
                    {/* Stats Cards - Full Width */}
                    <Grid item xs={12} sx={{ mb: 2 }}>
                        <Box sx={{ maxWidth: '600px', margin: '0 auto' }}> {/* Center stats cards */}
                            <Grid container spacing={2}>
                                <Grid item xs={6}>
                                    <Paper
                                        sx={{
                                            p: 2,
                                            background: 'rgba(220, 20, 60, 0.1)',
                                            borderRadius: 3,
                                            border: '1px solid rgba(220, 20, 60, 0.2)',
                                        }}
                                    >
                                        <Typography variant="h3" sx={{ color: '#DC143C', fontWeight: 600 }}>
                                            {tasks.length}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                                            Total Tasks
                                        </Typography>
                                    </Paper>
                                </Grid>
                                <Grid item xs={6}>
                                    <Paper
                                        sx={{
                                            p: 2,
                                            background: 'rgba(255, 255, 255, 0.03)',
                                            borderRadius: 3,
                                            border: '1px solid rgba(255, 255, 255, 0.1)',
                                        }}
                                    >
                                        <Typography variant="h3" sx={{ color: '#fff', fontWeight: 600 }}>
                                            {tasks.filter(t => t.priority === 'high').length}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                                            High Priority
                                        </Typography>
                                    </Paper>
                                </Grid>
                            </Grid>
                        </Box>
                    </Grid>

                    {/* Task List */}
                    <Grid item xs={12} md={8}>
                        <Paper
                            elevation={0}
                            sx={{
                                p: 2.5,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                maxWidth: '700px',
                                margin: '0 auto',
                            }}
                        >
                            <Box sx={{ 
                                display: 'flex', 
                                justifyContent: 'space-between', 
                                alignItems: 'center',
                                mb: 2,
                                px: 1
                            }}>
                                <Typography variant="h6" sx={{ color: '#fff', fontWeight: 500 }}>
                                    Your Tasks
                                </Typography>
                                <Chip 
                                    label={`${tasks.length} tasks`}
                                    size="small"
                                    sx={{
                                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                                        color: '#fff',
                                        height: '24px'
                                    }}
                                />
                            </Box>

                            {loading ? (
                                <Box sx={{ display: 'flex', justifyContent: 'center', py: 3 }}>
                                    <CircularProgress size={24} />
                                </Box>
                            ) : tasks.length === 0 ? (
                                <Box sx={{ 
                                    py: 3, 
                                    textAlign: 'center',
                                    color: 'rgba(255, 255, 255, 0.5)'
                                }}>
                                    No tasks yet. Create your first task!
                                </Box>
                            ) : (
                                <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                                    {tasks.map((task) => (
                                        <Box
                                            key={task.id}
                                            sx={{
                                                py: 1.5,
                                                px: 2,
                                                borderBottom: '1px solid rgba(255, 255, 255, 0.05)',
                                                transition: 'all 0.2s ease-in-out',
                                                '&:hover': {
                                                    backgroundColor: 'rgba(255, 255, 255, 0.02)',
                                                },
                                                '&:last-child': {
                                                    borderBottom: 'none'
                                                }
                                            }}
                                        >
                                            <Box sx={{ 
                                                display: 'flex', 
                                                justifyContent: 'space-between', 
                                                alignItems: 'flex-start',
                                                gap: 2
                                            }}>
                                                <Box sx={{ flex: 1 }}>
                                                    <Box sx={{ 
                                                        display: 'flex', 
                                                        alignItems: 'center', 
                                                        gap: 1,
                                                        mb: 0.5
                                                    }}>
                                                        <Typography 
                                                            sx={{ 
                                                                color: '#fff',
                                                                fontSize: '0.95rem',
                                                                fontWeight: 500
                                                            }}
                                                        >
                                                            {task.title}
                                                        </Typography>
                                                        <Chip
                                                            label={task.priority}
                                                            size="small"
                                                            sx={{
                                                                height: '20px',
                                                                backgroundColor: `${priorities.find(p => p.value === task.priority)?.color}15`,
                                                                color: priorities.find(p => p.value === task.priority)?.color,
                                                                fontSize: '0.75rem',
                                                                fontWeight: 500,
                                                                textTransform: 'capitalize',
                                                                '& .MuiChip-label': { px: 1 }
                                                            }}
                                                        />
                                                    </Box>

                                                    {task.subject && (
                                                        <Typography 
                                                            variant="body2" 
                                                            sx={{ 
                                                                color: 'rgba(255, 255, 255, 0.5)',
                                                                fontSize: '0.8rem',
                                                                mb: 0.5
                                                            }}
                                                        >
                                                            {task.subject}
                                                        </Typography>
                                                    )}

                                                    {task.description && (
                                                        <Typography 
                                                            variant="body2" 
                                                            sx={{ 
                                                                color: 'rgba(255, 255, 255, 0.4)',
                                                                fontSize: '0.85rem',
                                                                lineHeight: '1.3'
                                                            }}
                                                        >
                                                            {task.description}
                                                        </Typography>
                                                    )}
                                                </Box>

                                                <Box sx={{ 
                                                    display: 'flex', 
                                                    alignItems: 'center',
                                                    gap: 2
                                                }}>
                                                    {task.estimatedTime && (
                                                        <Typography 
                                                            variant="body2" 
                                                            sx={{ 
                                                                color: 'rgba(255, 255, 255, 0.4)',
                                                                fontSize: '0.8rem',
                                                                whiteSpace: 'nowrap'
                                                            }}
                                                        >
                                                            ‚è± {task.estimatedTime}h
                                                        </Typography>
                                                    )}
                                                    <IconButton
                                                        size="small"
                                                        onClick={() => handleDelete(task.id)}
                                                        sx={{ 
                                                            color: 'rgba(255, 255, 255, 0.3)',
                                                            padding: '4px',
                                                            '&:hover': {
                                                                color: '#DC143C',
                                                                backgroundColor: 'rgba(220, 20, 60, 0.1)',
                                                            }
                                                        }}
                                                    >
                                                        <DeleteIcon sx={{ fontSize: '1.1rem' }} />
                                                    </IconButton>
                                                </Box>
                                            </Box>
                                        </Box>
                                    ))}
                                </Box>
                            )}
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default TaskScheduler;
