import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Link,
  Alert,
  InputAdornment,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext'; // Add this import

const API_URL = 'http://127.0.0.1:5000';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();
  const { login, logout } = useAuth(); // Add this

  // Clear authentication when component mounts
  useEffect(() => {
    logout();
  }, [logout]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post(
        `${API_URL}/login`,
        {
          email: formData.email,
          password: formData.password
        },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.access_token) {
        login(response.data.access_token); // Use context login instead of localStorage
        setSuccess('Login successful! Redirecting...');

        // Clear form
        setFormData({
          email: '',
          password: ''
        });

        // Redirect to dashboard
        setTimeout(() => {
          navigate('/dashboard');
        }, 1500);
      }
    } catch (err) {
      console.error('Login error:', err);
      if (!err.response) {
        setError('Unable to connect to the server. Please try again later.');
      } else {
        setError(err.response?.data?.error || 'Invalid credentials. Please try again.');
      }
    }
  };

  const handleRegisterClick = (e) => {
    e.preventDefault();
    logout(); // Clear auth state before navigating
    navigate('/register');
  };

  return (
    <Box
      sx={{
        minHeight: 'calc(100vh - 64px)',
        background: '#F9F9F9',
        position: 'relative',
        overflow: 'hidden',
        display: 'flex',
        alignItems: 'center',
      }}
    >
      <Container maxWidth="xs">
        <Box
          sx={{
            position: 'relative',
            zIndex: 2,
            p: 4,
            background: '#FFFFFF',
            borderRadius: 2,
            border: '1px solid #E0E6ED',
            boxShadow: '0 4px 24px 0 rgba(0, 122, 255, 0.06)',
          }}
        >
          <Typography
            variant="h3"
            sx={{
              mb: 4,
              textAlign: 'center',
              fontWeight: 700,
              color: '#007AFF',
              letterSpacing: '-0.02em',
            }}
          >
            Welcome Back
          </Typography>

          {error && (
            <Alert 
              severity="error" 
              sx={{ 
                mb: 2,
                backgroundColor: 'rgba(0, 122, 255, 0.08)',
                color: '#007AFF',
              }}
            >
              {error}
            </Alert>
          )}

          {success && (
            <Alert 
              severity="success" 
              sx={{ 
                mb: 2,
                backgroundColor: 'rgba(50, 205, 50, 0.10)',
                color: '#32CD32',
              }}
            >
              {success}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit}>
            <Box sx={{ mb: 3 }}>
              <TextField
                required
                fullWidth
                label="Email"
                name="email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <EmailOutlinedIcon sx={{ color: '#007AFF' }} />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>

            <Box sx={{ mb: 4 }}>
              <TextField
                required
                fullWidth
                label="Password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <LockOutlinedIcon sx={{ color: '#007AFF' }} />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                py: 2,
                mb: 3,
                fontSize: '1.1rem',
                fontWeight: 600,
                background: 'linear-gradient(135deg, #007AFF 0%, #32CD32 100%)',
                borderRadius: 2,
                boxShadow: '0 4px 20px rgba(0, 122, 255, 0.08)',
                color: '#fff',
                transition: 'all 0.3s ease',
                '&:hover': {
                  background: 'linear-gradient(135deg, #32CD32 0%, #007AFF 100%)',
                  boxShadow: '0 6px 25px rgba(50, 205, 50, 0.12)',
                },
              }}
            >
              Sign In
            </Button>

            <Box
              sx={{
                textAlign: 'center',
                position: 'relative',
                '&::before, &::after': {
                  content: '""',
                  position: 'absolute',
                  top: '50%',
                  width: '30%',
                  height: '1px',
                  background: 'linear-gradient(90deg, transparent, #E0E6ED)',
                },
                '&::before': {
                  left: 0,
                },
                '&::after': {
                  right: 0,
                  transform: 'rotate(180deg)',
                },
              }}
            >
              <Link
                component="button"
                variant="button"
                onClick={handleRegisterClick} // Updated to use new handler
                sx={{
                  color: '#007AFF',
                  textDecoration: 'none',
                  fontWeight: 600,
                  transition: 'all 0.2s',
                  '&:hover': {
                    color: '#32CD32',
                  },
                }}
              >
                Don't have an account? Sign up
              </Link>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
}

export default Login;
