import React, { useState } from 'react';
import { 
  Container, 
  TextField, 
  Button, 
  Typography, 
  Box,
  Link,
  Alert
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import axios from 'axios';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [errors, setErrors] = useState({});
  const [apiError, setApiError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prevErrors => ({
        ...prevErrors,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setApiError('');
    setSuccess('');

    if (validateForm()) {
      try {
        console.log('Attempting login with:', formData.email);

        // Create form data (backend expects form data for login)
        const formDataObj = new URLSearchParams();
        formDataObj.append('username', formData.email); // backend expects 'username' for email
        formDataObj.append('password', formData.password);

        const response = await axios.post(
          'http://localhost:8000/api/v1/auth/auth/login',
          formDataObj,
          {
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded'
            }
          }
        );

        console.log('Login successful:', response.data);

        // Store the token
        localStorage.setItem('token', response.data.access_token);

        setSuccess('Login successful! Redirecting...');

        // Redirect to dashboard after successful login
        setTimeout(() => {
          navigate('/dashboard');
        }, 1500);

      } catch (err) {
        console.error('Login error:', err);
        setApiError(err.response?.data?.detail || 'Invalid email or password');
      }
    }
  };

  return (
    <Box
      sx={{
        minHeight: 'calc(100vh - 64px)',
        background: '#0A0A0F',
        position: 'relative',
        overflow: 'hidden',
        display: 'flex',
        alignItems: 'center',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: '50%',
          left: '0',
          right: '0',
          height: '40%',
          background: 'linear-gradient(90deg, #DC143C 0%, transparent 100%)',
          opacity: 0.07,
          transform: 'translateY(-50%) skewY(-12deg)',
          animation: 'pulse 4s infinite',
        },
      }}
    >
      <Container maxWidth="xs">
        <Box
          sx={{
            position: 'relative',
            zIndex: 2,
            p: 4,
            backdropFilter: 'blur(10px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.1)',
          }}
        >
          <Typography
            variant="h3"
            sx={{
              mb: 4,
              textAlign: 'center',
              fontWeight: 700,
              background: 'linear-gradient(45deg, #FFFFFF, #DC143C)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              letterSpacing: '-0.02em',
            }}
          >
            Welcome Back
          </Typography>

          {apiError && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {apiError}
            </Alert>
          )}

          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              {success}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit}>
            <Box sx={{ position: 'relative', mb: 3 }}>
              <Box
                sx={{
                  position: 'absolute',
                  left: '-40px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: '30px',
                  height: '2px',
                  background: 'linear-gradient(90deg, #DC143C, transparent)',
                }}
              />
              <TextField
                required
                fullWidth
                label="Email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                error={!!errors.email}
                helperText={errors.email}
                InputProps={{
                  startAdornment: (
                    <EmailOutlinedIcon 
                      sx={{ 
                        mr: 2, 
                        color: 'rgba(255, 255, 255, 0.5)'
                      }} 
                    />
                  ),
                }}
              />
            </Box>

            <Box sx={{ position: 'relative', mb: 4 }}>
              <Box
                sx={{
                  position: 'absolute',
                  left: '-40px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  width: '30px',
                  height: '2px',
                  background: 'linear-gradient(90deg, #DC143C, transparent)',
                }}
              />
              <TextField
                required
                fullWidth
                label="Password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                error={!!errors.password}
                helperText={errors.password}
                InputProps={{
                  startAdornment: (
                    <LockOutlinedIcon 
                      sx={{ 
                        mr: 2, 
                        color: 'rgba(255, 255, 255, 0.5)'
                      }} 
                    />
                  ),
                }}
              />
            </Box>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                py: 2,
                mb: 3,
                fontSize: '1.1rem',
                fontWeight: 600,
                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                borderRadius: '12px',
                boxShadow: '0 4px 20px rgba(220, 20, 60, 0.4)',
                transition: 'all 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: '0 6px 25px rgba(220, 20, 60, 0.5)',
                },
              }}
            >
              Sign In
            </Button>

            <Box
              sx={{
                textAlign: 'center',
                position: 'relative',
                '&::before, &::after': {
                  content: '""',
                  position: 'absolute',
                  top: '50%',
                  width: '30%',
                  height: '1px',
                  background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.1))',
                },
                '&::before': {
                  left: 0,
                },
                '&::after': {
                  right: 0,
                  transform: 'rotate(180deg)',
                },
              }}
            >
              <Link
                component="button"
                variant="body2"
                onClick={() => navigate('/register')}
                sx={{
                  color: 'rgba(255, 255, 255, 0.7)',
                  textDecoration: 'none',
                  transition: 'all 0.2s',
                  '&:hover': {
                    color: '#DC143C',
                  },
                }}
              >
                Don't have an account? Sign up
              </Link>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
}

export default Login;
