import React, { useState, useEffect } from 'react';
import {
    Box,
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Chip,
    IconButton,
    Alert,
    CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

const priorities = [
    { value: 'high', label: 'High', color: '#DC143C' },
    { value: 'medium', label: 'Medium', color: '#FFA500' },
    { value: 'low', label: 'Low', color: '#4CAF50' }
];

function TaskScheduler() {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        subject: '',
        priority: 'medium',
        estimatedTime: ''
    });

    // Fetch tasks on component mount
    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            console.log('Using token:', token); // Debug log

            const response = await axios.get(`${API_URL}/tasks`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            console.log('Response:', response.data); // Debug log
            setTasks(response.data);
        } catch (err) {
            console.error('Full error:', err); // More detailed error logging
            setError(err.response?.data?.error || 'Failed to fetch tasks');
        } finally {
            setLoading(false);
        }
    };    

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setLoading(true);

        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_URL}/tasks`,
                formData,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            setTasks([...tasks, response.data]);
            setSuccess('Task added successfully!');
            setFormData({
                title: '',
                description: '',
                subject: '',
                priority: 'medium',
                estimatedTime: ''
            });
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add task');
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (taskId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`${API_URL}/tasks/${taskId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setTasks(tasks.filter(task => task.id !== taskId));
            setSuccess('Task deleted successfully!');
        } catch (err) {
            setError('Failed to delete task');
        }
    };

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4,
                px: 3
            }}
        >
            <Container maxWidth="xl">
                <Grid container spacing={4}>
                    {/* Left Panel - Task Stats & Creation */}
                    <Grid item xs={12} md={4}>
                        {/* Quick Stats */}
                        <Box sx={{ mb: 4 }}>
                            <Grid container spacing={2}>
                                <Grid item xs={6}>
                                    <Paper
                                        sx={{
                                            p: 2,
                                            background: 'rgba(220, 20, 60, 0.1)',
                                            borderRadius: 3,
                                            border: '1px solid rgba(220, 20, 60, 0.2)',
                                        }}
                                    >
                                        <Typography variant="h3" sx={{ color: '#DC143C', fontWeight: 600 }}>
                                            {tasks.length}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                                            Total Tasks
                                        </Typography>
                                    </Paper>
                                </Grid>
                                <Grid item xs={6}>
                                    <Paper
                                        sx={{
                                            p: 2,
                                            background: 'rgba(255, 255, 255, 0.03)',
                                            borderRadius: 3,
                                            border: '1px solid rgba(255, 255, 255, 0.1)',
                                        }}
                                    >
                                        <Typography variant="h3" sx={{ color: '#fff', fontWeight: 600 }}>
                                            {tasks.filter(t => t.priority === 'high').length}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                                            High Priority
                                        </Typography>
                                    </Paper>
                                </Grid>
                            </Grid>
                        </Box>

                        {/* Task Creation Form */}
                        <Paper
                            elevation={0}
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                position: 'sticky',
                                top: 24
                            }}
                        >
                            {/* ... (keep form content with enhanced styling) ... */}
                        </Paper>
                    </Grid>

                    {/* Right Panel - Task List */}
                    <Grid item xs={12} md={8}>
                        <Paper
                            elevation={0}
                            sx={{
                                background: 'transparent',
                                borderRadius: 4,
                            }}
                        >
                            {/* Task Filters */}
                            <Box 
                                sx={{ 
                                    mb: 4, 
                                    display: 'flex', 
                                    gap: 2,
                                    flexWrap: 'wrap'
                                }}
                            >
                                {['All', 'High', 'Medium', 'Low'].map((filter) => (
                                    <Chip
                                        key={filter}
                                        label={filter}
                                        sx={{
                                            backgroundColor: 'rgba(255, 255, 255, 0.05)',
                                            color: '#fff',
                                            borderRadius: 2,
                                            '&:hover': {
                                                backgroundColor: 'rgba(220, 20, 60, 0.2)',
                                            }
                                        }}
                                    />
                                ))}
                            </Box>

                            {/* Tasks Grid */}
                            {loading ? (
                                <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
                                    <CircularProgress sx={{ color: '#DC143C' }} />
                                </Box>
                            ) : tasks.length === 0 ? (
                                <Box
                                    sx={{
                                        textAlign: 'center',
                                        py: 8,
                                        background: 'rgba(17, 17, 17, 0.6)',
                                        borderRadius: 4,
                                        border: '1px solid rgba(255, 255, 255, 0.1)',
                                    }}
                                >
                                    <Typography variant="h6" sx={{ color: '#fff', mb: 1 }}>
                                        No tasks yet
                                    </Typography>
                                    <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.5)' }}>
                                        Create your first task to get started
                                    </Typography>
                                </Box>
                            ) : (
                                <Grid container spacing={2}>
                                    {tasks.map((task) => (
                                        <Grid item xs={12} sm={6} key={task.id}>
                                            <Paper
                                                sx={{
                                                    p: 3,
                                                    background: 'rgba(17, 17, 17, 0.6)',
                                                    borderRadius: 3,
                                                    border: '1px solid rgba(255, 255, 255, 0.1)',
                                                    transition: 'all 0.3s ease',
                                                    '&:hover': {
                                                        transform: 'translateY(-4px)',
                                                        boxShadow: '0 12px 20px rgba(0,0,0,0.2)',
                                                        border: '1px solid rgba(220, 20, 60, 0.3)',
                                                    }
                                                }}
                                            >
                                                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                                                    <Typography variant="h6" sx={{ color: '#fff', fontWeight: 500 }}>
                                                        {task.title}
                                                    </Typography>
                                                    <IconButton
                                                        size="small"
                                                        onClick={() => handleDelete(task.id)}
                                                        sx={{ 
                                                            color: 'rgba(255,255,255,0.5)',
                                                            '&:hover': {
                                                                color: '#DC143C',
                                                                backgroundColor: 'rgba(220, 20, 60, 0.1)',
                                                            }
                                                        }}
                                                    >
                                                        <DeleteIcon />
                                                    </IconButton>
                                                </Box>

                                                <Typography 
                                                    variant="body2" 
                                                    sx={{ 
                                                        color: 'rgba(255,255,255,0.7)',
                                                        mb: 2,
                                                        height: '40px',
                                                        overflow: 'hidden',
                                                        textOverflow: 'ellipsis',
                                                        display: '-webkit-box',
                                                        WebkitLineClamp: 2,
                                                        WebkitBoxOrient: 'vertical',
                                                    }}
                                                >
                                                    {task.description}
                                                </Typography>

                                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                                                    <Chip
                                                        label={task.priority}
                                                        size="small"
                                                        sx={{
                                                            backgroundColor: `${priorities.find(p => p.value === task.priority)?.color}20`,
                                                            color: priorities.find(p => p.value === task.priority)?.color,
                                                            fontWeight: 500,
                                                            textTransform: 'capitalize'
                                                        }}
                                                    />
                                                    {task.estimatedTime && (
                                                        <Typography 
                                                            variant="body2" 
                                                            sx={{ 
                                                                color: 'rgba(255,255,255,0.5)',
                                                                display: 'flex',
                                                                alignItems: 'center',
                                                                gap: 1
                                                            }}
                                                        >
                                                            <span>‚è±</span>
                                                            {task.estimatedTime}h
                                                        </Typography>
                                                    )}
                                                </Box>
                                            </Paper>
                                        </Grid>
                                    ))}
                                </Grid>
                            )}
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}


export default TaskScheduler;
