import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Link,
  Alert,
  InputAdornment,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext'; // Add this import

const API_URL = 'http://127.0.0.1:5000';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();
  const { login, logout } = useAuth(); // Add this

  // Clear authentication when component mounts
  useEffect(() => {
    logout();
  }, [logout]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post(
        `${API_URL}/login`,
        {
          email: formData.email,
          password: formData.password
        },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.access_token) {
        login(response.data.access_token); // Use context login instead of localStorage
        setSuccess('Login successful! Redirecting...');

        // Clear form
        setFormData({
          email: '',
          password: ''
        });

        // Redirect to dashboard
        setTimeout(() => {
          navigate('/dashboard');
        }, 1500);
      }
    } catch (err) {
      console.error('Login error:', err);
      if (!err.response) {
        setError('Unable to connect to the server. Please try again later.');
      } else {
        setError(err.response?.data?.error || 'Invalid credentials. Please try again.');
      }
    }
  };

  const handleRegisterClick = (e) => {
    e.preventDefault();
    logout(); // Clear auth state before navigating
    navigate('/register');
  };

  return (
    <Box
      sx={{
        minHeight: 'calc(100vh - 64px)',
        background: '#0A0A0F',
        position: 'relative',
        overflow: 'hidden',
        display: 'flex',
        alignItems: 'center',
        '&::before, &::after': {
          content: '""',
          position: 'absolute',
          width: '50%',
          height: '50%',
          borderRadius: '50%',
          background: 'radial-gradient(circle, #DC143C 0%, transparent 70%)',
          opacity: 0.05,
          animation: 'float 20s infinite ease-in-out',
        },
        '&::before': {
          top: '-25%',
          right: '-25%',
        },
        '&::after': {
          bottom: '-25%',
          left: '-25%',
          animationDelay: '-10s',
        },
        '@keyframes float': {
          '0%, 100%': {
            transform: 'translate(0, 0)',
          },
          '50%': {
            transform: 'translate(5%, 5%)',
          },
        },
      }}
    >
      <Container maxWidth="xs">
        <Box
          sx={{
            position: 'relative',
            zIndex: 2,
            p: 4,
            backdropFilter: 'blur(10px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              right: 0,
              width: '100px',
              height: '100px',
              background: 'radial-gradient(circle, #DC143C 0%, transparent 70%)',
              opacity: 0.1,
              borderRadius: '50%',
              transform: 'translate(50%, -50%)',
            },
          }}
        >
          <Typography
            variant="h3"
            sx={{
              mb: 4,
              textAlign: 'center',
              fontWeight: 700,
              background: 'linear-gradient(45deg, #FFFFFF, #DC143C)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              letterSpacing: '-0.02em',
            }}
          >
            Welcome Back
          </Typography>

          {error && (
            <Alert 
              severity="error" 
              sx={{ 
                mb: 2,
                backgroundColor: 'rgba(220, 20, 60, 0.1)',
                color: '#FF6B6B'
              }}
            >
              {error}
            </Alert>
          )}

          {success && (
            <Alert 
              severity="success" 
              sx={{ 
                mb: 2,
                backgroundColor: 'rgba(76, 175, 80, 0.1)',
                color: '#4CAF50'
              }}
            >
              {success}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit}>
            <Box sx={{ mb: 3 }}>
              <TextField
                required
                fullWidth
                label="Email"
                name="email"
                type="email"
                value={formData.email}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <EmailOutlinedIcon sx={{ color: 'rgba(255, 255, 255, 0.5)' }} />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>

            <Box sx={{ mb: 4 }}>
              <TextField
                required
                fullWidth
                label="Password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <LockOutlinedIcon sx={{ color: 'rgba(255, 255, 255, 0.5)' }} />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                py: 2,
                mb: 3,
                fontSize: '1.1rem',
                fontWeight: 600,
                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                borderRadius: '12px',
                boxShadow: '0 4px 20px rgba(220, 20, 60, 0.4)',
                transition: 'all 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: '0 6px 25px rgba(220, 20, 60, 0.5)',
                },
              }}
            >
              Sign In
            </Button>

            <Box
              sx={{
                textAlign: 'center',
                position: 'relative',
                '&::before, &::after': {
                  content: '""',
                  position: 'absolute',
                  top: '50%',
                  width: '30%',
                  height: '1px',
                  background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.1))',
                },
                '&::before': {
                  left: 0,
                },
                '&::after': {
                  right: 0,
                  transform: 'rotate(180deg)',
                },
              }}
            >
              <Link
                component="button"
                variant="button"
                onClick={handleRegisterClick} // Updated to use new handler
                sx={{
                  color: 'rgba(255, 255, 255, 0.7)',
                  textDecoration: 'none',
                  transition: 'all 0.2s',
                  '&:hover': {
                    color: '#DC143C',
                  },
                }}
              >
                Don't have an account? Sign up
              </Link>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
}

export default Login;
