import React from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Button, 
  Grid, 
  Card, 
  CardContent,
  useTheme,
  IconButton 
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import AutoGraphIcon from '@mui/icons-material/AutoGraph';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

function Home() {
  const theme = useTheme();
  const navigate = useNavigate();

  const scrollToFeatures = () => {
    const featuresSection = document.getElementById('features');
    featuresSection.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <Box sx={{ minHeight: '100vh' }}>
      {/* Full Screen Hero Section */}
      <Box
        sx={{
          position: 'relative',
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
          bgcolor: '#F8FAFC',
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'radial-gradient(circle at top right, rgba(44, 123, 229, 0.15), transparent 50%)',
            zIndex: 1,
          }
        }}
      >
        <Container maxWidth="lg" sx={{ position: 'relative', zIndex: 2 }}>
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={7}>
              <Box sx={{
                textAlign: { xs: 'center', md: 'left' },
                mt: { xs: -8, md: -4 }
              }}>
                <Typography
                  variant="h1"
                  sx={{
                    fontSize: { xs: '2.5rem', md: '3.5rem', lg: '4rem' },
                    mb: 2,
                    background: 'linear-gradient(to right, #2C7BE5, #17B897)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    fontWeight: 700,
                  }}
                >
                  Master Your Time
                </Typography>
                <Typography
                  variant="h2"
                  sx={{
                    fontSize: { xs: '1.5rem', md: '2rem' },
                    mb: 4,
                    color: '#1A365D',
                    fontWeight: 500,
                  }}
                >
                  AI-Powered Study Schedule Optimization
                </Typography>
                <Box sx={{
                  display: 'flex',
                  gap: 2,
                  justifyContent: { xs: 'center', md: 'flex-start' }
                }}>
                  <Button
                    variant="contained"
                    size="large"
                    onClick={() => navigate('/register')}
                    sx={{
                      py: 2,
                      px: 4,
                      fontSize: '1.1rem',
                      bgcolor: '#2C7BE5',
                      '&:hover': {
                        bgcolor: '#004D96',
                      }
                    }}
                  >
                    Get Started
                  </Button>
                  <Button
                    variant="outlined"
                    size="large"
                    onClick={() => navigate('/login')}
                    sx={{
                      py: 2,
                      px: 4,
                      fontSize: '1.1rem',
                      borderWidth: '2px',
                      borderColor: '#2C7BE5',
                      color: '#2C7BE5',
                      '&:hover': {
                        borderColor: '#004D96',
                        color: '#004D96',
                        bgcolor: 'rgba(44, 123, 229, 0.1)',
                      }
                    }}
                  >
                    Login
                  </Button>
                </Box>
              </Box>
            </Grid>
          </Grid>
        </Container>
        {/* Scroll Down Indicator */}
        <Box
          sx={{
            position: 'absolute',
            bottom: 40,
            left: '50%',
            transform: 'translateX(-50%)',
            zIndex: 2,
            textAlign: 'center',
            cursor: 'pointer',
          }}
          onClick={scrollToFeatures}
        >
          <Typography
            variant="body2"
            sx={{
              color: '#1A365D',
              mb: 1,
              fontSize: '0.9rem',
              textTransform: 'uppercase',
              letterSpacing: '0.1em',
            }}
          >
            Scroll Down
          </Typography>
          <IconButton
            sx={{
              color: '#2C7BE5',
              animation: 'bounce 2s infinite',
              '@keyframes bounce': {
                '0%, 20%, 50%, 80%, 100%': {
                  transform: 'translateY(0)',
                },
                '40%': {
                  transform: 'translateY(-10px)',
                },
                '60%': {
                  transform: 'translateY(-5px)',
                },
              },
            }}
          >
            <KeyboardArrowDownIcon />
          </IconButton>
        </Box>
      </Box>

      {/* Features Section */}
      <Box id="features" sx={{ py: { xs: 8, md: 12 }, bgcolor: 'white' }}>
        <Container maxWidth="lg">
          <Typography
            variant="h3"
            align="center"
            sx={{
              mb: { xs: 6, md: 8 },
              fontSize: { xs: '2rem', md: '2.5rem' },
              fontWeight: 600,
              color: '#1A365D',
            }}
          >
            Why Choose Tempo?
          </Typography>
          <Grid container spacing={4}>
            {[
              {
                icon: <AccessTimeIcon sx={{ fontSize: 40, color: '#2C7BE5' }} />,
                title: "Smart Scheduling",
                description: "AI-powered scheduling that adapts to your study habits and preferences."
              },
              {
                icon: <AutoGraphIcon sx={{ fontSize: 40, color: '#2C7BE5' }} />,
                title: "Performance Analytics",
                description: "Track your progress and optimize your study efficiency with real-time insights."
              },
              {
                icon: <CalendarMonthIcon sx={{ fontSize: 40, color: '#2C7BE5' }} />,
                title: "Seamless Planning",
                description: "Effortlessly manage your academic schedule with our intuitive interface."
              }
            ].map((feature, index) => (
              <Grid item xs={12} md={4} key={index}>
                <Card
                  sx={{
                    height: '100%',
                    position: 'relative',
                    overflow: 'hidden',
                    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)',
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      transform: 'translateY(-5px)',
                      boxShadow: '0 8px 30px rgba(44, 123, 229, 0.15)',
                    }
                  }}
                >
                  <CardContent sx={{ p: 4, textAlign: 'center' }}>
                    <Box
                      sx={{
                        mb: 3,
                        display: 'inline-flex',
                        p: 2,
                        borderRadius: '50%',
                        background: 'rgba(44, 123, 229, 0.1)',
                      }}
                    >
                      {feature.icon}
                    </Box>
                    <Typography
                      variant="h5"
                      component="h3"
                      sx={{ mb: 2, fontWeight: 600, color: '#1A365D' }}
                    >
                      {feature.title}
                    </Typography>
                    <Typography
                      variant="body1"
                      sx={{
                        lineHeight: 1.7,
                        color: '#4A5568'
                      }}
                    >
                      {feature.description}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Call to Action Section */}
      <Box
        sx={{
          py: { xs: 8, md: 12 },
          position: 'relative',
          bgcolor: '#F8FAFC',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'radial-gradient(circle at bottom left, rgba(44, 123, 229, 0.15), transparent 50%)',
            zIndex: 1,
          }
        }}
      >
        <Container maxWidth="md" sx={{ position: 'relative', zIndex: 2 }}>
          <Box sx={{ textAlign: 'center' }}>
            <Typography
              variant="h3"
              sx={{
                mb: 3,
                fontSize: { xs: '2rem', md: '2.5rem' },
                fontWeight: 600,
                color: '#1A365D',
              }}
            >
              Ready to Transform Your Study Routine?
            </Typography>
            <Typography
              variant="h6"
              sx={{
                mb: 4,
                color: '#4A5568',
                fontSize: { xs: '1.1rem', md: '1.25rem' },
                fontWeight: 400,
                lineHeight: 1.6,
              }}
            >
              Join thousands of students who are maximizing their potential with Tempo.
            </Typography>
            <Button
              variant="contained"
              size="large"
              onClick={() => navigate('/register')}
              sx={{
                py: 2,
                px: 6,
                fontSize: '1.1rem',
                bgcolor: '#2C7BE5',
                '&:hover': {
                  bgcolor: '#004D96',
                }
              }}
            >
              Get Started Now
            </Button>
          </Box>
        </Container>
      </Box>
    </Box>
  );
}

export default Home;
