import React, { useState, useEffect } from 'react';
import { 
  Container, 
  TextField, 
  Button, 
  Typography, 
  Box,
  Link,
  Alert
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import VpnKeyOutlinedIcon from '@mui/icons-material/VpnKeyOutlined';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [errors, setErrors] = useState({});
  const [apiError, setApiError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  // Clear token on component mount
  useEffect(() => {
    localStorage.removeItem('token');
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    // Clear field-specific error
    if (errors[name]) {
      setErrors(prevErrors => ({
        ...prevErrors,
        [name]: ''
      }));
    }
    // Clear API error when user starts typing
    if (apiError) {
      setApiError('');
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setApiError('');
    setSuccess('');

    if (validateForm()) {
      try {
        const response = await axios.post(
          'http://localhost:8000/api/v1/auth/register',
          {
            email: formData.email,
            name: formData.name,
            password: formData.password
          }
        );

        console.log('Registration successful:', response.data);
        setSuccess('Registration successful! Redirecting to login...');

        // Clear form
        setFormData({
          name: '',
          email: '',
          password: '',
          confirmPassword: ''
        });

        // Redirect to login after delay
        setTimeout(() => {
          navigate('/login');
        }, 2000);

      } catch (err) {
        console.error('Registration error:', err);

        // Handle specific error cases
        if (err.response?.status === 400) {
          if (err.response.data?.detail?.includes('already registered')) {
            setErrors(prev => ({
              ...prev,
              email: 'This email is already registered'
            }));
          } else {
            setApiError(err.response.data?.detail || 'Invalid registration data');
          }
        } else {
          setApiError('Registration failed. Please try again later.');
        }
      }
    }
  };

  return (
    <Box
      sx={{
        minHeight: 'calc(100vh - 64px)',
        background: '#0A0A0F',
        position: 'relative',
        overflow: 'hidden',
        display: 'flex',
        alignItems: 'center',
        '&::before, &::after': {
          content: '""',
          position: 'absolute',
          width: '50%',
          height: '50%',
          borderRadius: '50%',
          background: 'radial-gradient(circle, #DC143C 0%, transparent 70%)',
          opacity: 0.05,
          animation: 'float 20s infinite ease-in-out',
        },
        '&::before': {
          top: '-25%',
          right: '-25%',
        },
        '&::after': {
          bottom: '-25%',
          left: '-25%',
          animationDelay: '-10s',
        },
        '@keyframes float': {
          '0%, 100%': {
            transform: 'translate(0, 0)',
          },
          '50%': {
            transform: 'translate(5%, 5%)',
          },
        },
      }}
    >
      <Container maxWidth="xs">
        <Box
          sx={{
            position: 'relative',
            zIndex: 2,
            p: 4,
            backdropFilter: 'blur(10px)',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              right: 0,
              width: '100px',
              height: '100px',
              background: 'radial-gradient(circle, #DC143C 0%, transparent 70%)',
              opacity: 0.1,
              borderRadius: '50%',
              transform: 'translate(50%, -50%)',
            },
          }}
        >
          <Typography
            variant="h3"
            sx={{
              mb: 4,
              textAlign: 'center',
              fontWeight: 700,
              background: 'linear-gradient(45deg, #FFFFFF, #DC143C)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              letterSpacing: '-0.02em',
            }}
          >
            Create Account
          </Typography>

          {apiError && (
            <Alert 
              severity="error" 
              sx={{ 
                mb: 2,
                backgroundColor: 'rgba(220, 20, 60, 0.1)',
                color: '#FF6B6B'
              }}
            >
              {apiError}
            </Alert>
          )}

          {success && (
            <Alert 
              severity="success" 
              sx={{ 
                mb: 2,
                backgroundColor: 'rgba(76, 175, 80, 0.1)',
                color: '#4CAF50'
              }}
            >
              {success}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit}>
            {[
              {
                name: 'name',
                label: 'Full Name',
                icon: <PersonOutlineIcon />,
              },
              {
                name: 'email',
                label: 'Email',
                icon: <EmailOutlinedIcon />,
              },
              {
                name: 'password',
                label: 'Password',
                type: 'password',
                icon: <LockOutlinedIcon />,
              },
              {
                name: 'confirmPassword',
                label: 'Confirm Password',
                type: 'password',
                icon: <VpnKeyOutlinedIcon />,
              },
            ].map((field, index) => (
              <Box 
                key={field.name} 
                sx={{ 
                  position: 'relative',
                  mb: 3,
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    left: '-40px',
                    top: '50%',
                    transform: 'translateY(-50%)',
                    width: '30px',
                    height: '2px',
                    background: 'linear-gradient(90deg, #DC143C, transparent)',
                    opacity: index === 0 ? 1 : 0.5,
                  },
                }}
              >
                <TextField
                  required
                  fullWidth
                  label={field.label}
                  name={field.name}
                  type={field.type || 'text'}
                  value={formData[field.name]}
                  onChange={handleChange}
                  error={!!errors[field.name]}
                  helperText={errors[field.name]}
                  InputProps={{
                    startAdornment: (
                      <Box sx={{ mr: 2, color: 'rgba(255, 255, 255, 0.5)' }}>
                        {field.icon}
                      </Box>
                    ),
                  }}
                />
              </Box>
            ))}

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                py: 2,
                mb: 3,
                fontSize: '1.1rem',
                fontWeight: 600,
                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                borderRadius: '12px',
                boxShadow: '0 4px 20px rgba(220, 20, 60, 0.4)',
                transition: 'all 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: '0 6px 25px rgba(220, 20, 60, 0.5)',
                },
              }}
            >
              Create Account
            </Button>

            <Box
              sx={{
                textAlign: 'center',
                position: 'relative',
                '&::before, &::after': {
                  content: '""',
                  position: 'absolute',
                  top: '50%',
                  width: '30%',
                  height: '1px',
                  background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.1))',
                },
                '&::before': {
                  left: 0,
                },
                '&::after': {
                  right: 0,
                  transform: 'rotate(180deg)',
                },
              }}
            >
              <Link
                component="button"
                variant="body2"
                onClick={() => navigate('/login')}
                sx={{
                  color: 'rgba(255, 255, 255, 0.7)',
                  textDecoration: 'none',
                  transition: 'all 0.2s',
                  '&:hover': {
                    color: '#DC143C',
                  },
                }}
              >
                Already have an account? Sign in
              </Link>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
}

export default Register;
