import React, { useState, useEffect } from 'react';
import './App.css';
import EventSignupForm from './components/EventSignupForm';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [isLoading, setIsLoading] = useState(false);

  // Create background particles
  useEffect(() => {
    const createBackgroundParticle = () => {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.animationDelay = Math.random() * 10 + 's';
      document.querySelector('.background-particles').appendChild(particle);

      // Remove particle after animation
      setTimeout(() => {
        if (particle.parentNode) {
          particle.parentNode.removeChild(particle);
        }
      }, 10000);
    };

    // Create particles periodically
    const particleInterval = setInterval(createBackgroundParticle, 3000);

    // Create initial particles
    for (let i = 0; i < 10; i++) {
      setTimeout(createBackgroundParticle, i * 1000);
    }

    return () => clearInterval(particleInterval);
  }, []);

  const handleExport = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:5001/api/export', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `ganesh_utsav_signups_${new Date().toISOString().slice(0, 10)}.csv`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
        toast.success('🎉 CSV export completed successfully! 🎉');

        // Add celebration effect
        createCelebration();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Export failed');
      }
    } catch (error) {
      toast.error('Export failed: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const createCelebration = () => {
    const celebration = document.createElement('div');
    celebration.className = 'success-celebration';

    const elements = ['🎉', '✨', '🎊', '🌟'];
    elements.forEach((element, index) => {
      const celebrationElement = document.createElement('div');
      celebrationElement.className = 'celebration-element';
      celebrationElement.textContent = element;
      celebrationElement.style.animationDelay = `${index * 0.2}s`;
      celebration.appendChild(celebrationElement);
    });

    document.body.appendChild(celebration);

    setTimeout(() => {
      if (celebration.parentNode) {
        celebration.parentNode.removeChild(celebration);
      }
    }, 3000);
  };

  return (
    <div className="App cultural-theme">
      {/* Background Particles */}
      <div className="background-particles"></div>

      {/* Floating Cultural Elements */}
      <div className="floating-cultural">
        <div className="cultural-element">🕉️</div>
        <div className="cultural-element">🌸</div>
        <div className="cultural-element">🎪</div>
        <div className="cultural-element">🎭</div>
        <div className="cultural-element">✨</div>
        <div className="cultural-element">🎨</div>
      </div>

      <main className="App-main">
        <EventSignupForm />

        <div className="export-section">
          <button 
            onClick={handleExport}
            disabled={isLoading}
            className={`export-button ${isLoading ? 'loading' : ''}`}
          >
            {isLoading ? 'Processing... 🚀' : 'Export Signups to CSV 📊'}
          </button>
        </div>
      </main>

      <ToastContainer 
        position="top-right" 
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        toastStyle={{
          borderRadius: '15px',
          fontFamily: 'Poppins, sans-serif',
          fontSize: '14px',
          fontWeight: '500'
        }}
      />
    </div>
  );
}

export default App;
