import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import './EventSignupForm.css';

const EventSignupForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    performance_name: '',
    performance_type: '',
    duration: '',
    number_of_performers: '',
    performer_names: '',
    group: '',
    performance_description: '',
    coordinator_name: '',
    contact_number: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formErrors, setFormErrors] = useState({});
  const [isValid, setIsValid] = useState(false);

  // Create floating particles
  useEffect(() => {
    const createParticle = () => {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.animationDelay = Math.random() * 6 + 's';
      document.querySelector('.floating-particles').appendChild(particle);

      // Remove particle after animation
      setTimeout(() => {
        if (particle.parentNode) {
          particle.parentNode.removeChild(particle);
        }
      }, 6000);
    };

    // Create particles periodically
    const particleInterval = setInterval(createParticle, 2000);

    // Create initial particles
    for (let i = 0; i < 9; i++) {
      setTimeout(createParticle, i * 500);
    }

    return () => clearInterval(particleInterval);
  }, []);

  // Form validation
  useEffect(() => {
    const errors = {};
    const requiredFields = ['email', 'performance_name', 'performance_type', 'duration', 
                          'number_of_performers', 'performer_names', 'group', 
                          'coordinator_name', 'contact_number'];

    requiredFields.forEach(field => {
      if (!formData[field]) {
        errors[field] = 'This field is required';
      }
    });

    // Email validation
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Please enter a valid email';
    }

    // Phone validation
    if (formData.contact_number && !/^\d{10}$/.test(formData.contact_number.replace(/\D/g, ''))) {
      errors.contact_number = 'Please enter a valid 10-digit phone number';
    }

    setFormErrors(errors);
    setIsValid(Object.keys(errors).length === 0);
  }, [formData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Add fun interaction
    const input = e.target;
    input.classList.add('celebrate');
    setTimeout(() => input.classList.remove('celebrate'), 600);
  };

  const createConfetti = () => {
    for (let i = 0; i < 50; i++) {
      const confetti = document.createElement('div');
      confetti.className = 'confetti';
      confetti.style.left = Math.random() * 100 + '%';
      confetti.style.animationDelay = Math.random() * 3 + 's';
      confetti.style.backgroundColor = ['#EC4899', '#F59E0B', '#10B981', '#3B82F6'][Math.floor(Math.random() * 4)];
      document.body.appendChild(confetti);

      setTimeout(() => {
        if (confetti.parentNode) {
          confetti.parentNode.removeChild(confetti);
        }
      }, 3000);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isValid) {
      toast.error('Please fill in all required fields correctly');
      return;
    }

    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:5001/api/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Success celebration
        createConfetti();
        toast.success('üéâ Signup submitted successfully! üéâ');

        // Reset form with animation
        const form = document.querySelector('.signup-form');
        form.classList.add('success-animation');
        setTimeout(() => form.classList.remove('success-animation'), 600);

        setFormData({
          email: '',
          performance_name: '',
          performance_type: '',
          duration: '',
          number_of_performers: '',
          performer_names: '',
          group: '',
          performance_description: '',
          coordinator_name: '',
          contact_number: ''
        });
      } else {
        const error = await response.json();
        toast.error(error.error || 'Signup failed');
      }
    } catch (error) {
      toast.error('Signup failed: ' + error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleGenerateDescription = () => {
    const descriptions = [
      "A mesmerizing fusion of classical and contemporary dance styles that will transport the audience to a world of rhythm and grace.",
      "An electrifying musical performance that combines traditional instruments with modern beats, creating an unforgettable auditory experience.",
      "A powerful dramatic piece that explores themes of cultural identity and unity, performed with passion and authenticity.",
      "A soul-stirring poetry recitation that weaves together ancient wisdom with contemporary perspectives on life and spirituality.",
      "A hilarious comedy act that celebrates cultural diversity through humor, bringing joy and laughter to everyone in the audience.",
      "A magical performance that blends illusion with cultural storytelling, creating moments of wonder and amazement."
    ];

    const randomDescription = descriptions[Math.floor(Math.random() * descriptions.length)];
    setFormData(prev => ({
      ...prev,
      performance_description: randomDescription
    }));

    toast.info('‚ú® Generated a creative description for you!');
  };

  const getFieldClassName = (fieldName) => {
    const baseClass = 'form-group';
    if (formErrors[fieldName]) return `${baseClass} error`;
    if (formData[fieldName] && !formErrors[fieldName]) return `${baseClass} success`;
    return baseClass;
  };

  return (
    <div className="signup-form-container">
      {/* Floating Particles */}
      <div className="floating-particles"></div>

      {/* Fun Interactive Elements */}
      <div className="fun-element">üé™</div>
      <div className="fun-element">üé≠</div>
      <div className="fun-element">üé®</div>
      <div className="fun-element">‚ú®</div>

      <form onSubmit={handleSubmit} className="signup-form">
        <div className="form-header">
          <h1>Ganesh Utsav 2025 - Cultural Event Night</h1>
          <p>Sign up to showcase your talent! üåü</p>
        </div>

        <div className="form-section">
          <h2>Performance Details</h2>

          <div className={getFieldClassName('email')}>
            <label htmlFor="email">Email ID</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="your.email@example.com"
            />
            {formErrors.email && <span className="error-message">{formErrors.email}</span>}
          </div>

          <div className={getFieldClassName('performance_name')}>
            <label htmlFor="performance_name">Performance Name</label>
            <input
              type="text"
              id="performance_name"
              name="performance_name"
              value={formData.performance_name}
              onChange={handleChange}
              required
              placeholder="e.g., Bollywood Dance Medley"
            />
            {formErrors.performance_name && <span className="error-message">{formErrors.performance_name}</span>}
          </div>

          <div className={getFieldClassName('performance_type')}>
            <label htmlFor="performance_type">Performance Type</label>
            <select
              id="performance_type"
              name="performance_type"
              value={formData.performance_type}
              onChange={handleChange}
              required
            >
              <option value="">Select a type</option>
              <option value="Dance">Dance üíÉ</option>
              <option value="Music">Music üéµ</option>
              <option value="Drama">Drama üé≠</option>
              <option value="Poetry">Poetry üìù</option>
              <option value="Comedy">Comedy üòÑ</option>
              <option value="Magic">Magic ‚ú®</option>
              <option value="Other">Other üé™</option>
            </select>
            {formErrors.performance_type && <span className="error-message">{formErrors.performance_type}</span>}
          </div>

          <div className={getFieldClassName('duration')}>
            <label htmlFor="duration">Duration</label>
            <select
              id="duration"
              name="duration"
              value={formData.duration}
              onChange={handleChange}
              required
            >
              <option value="">Select duration</option>
              <option value="2-3 minutes">2-3 minutes ‚è±Ô∏è</option>
              <option value="3-5 minutes">3-5 minutes ‚è±Ô∏è</option>
              <option value="5-7 minutes">5-7 minutes ‚è±Ô∏è</option>
              <option value="7-10 minutes">7-10 minutes ‚è±Ô∏è</option>
              <option value="10+ minutes">10+ minutes ‚è±Ô∏è</option>
            </select>
            {formErrors.duration && <span className="error-message">{formErrors.duration}</span>}
          </div>

          <div className={getFieldClassName('number_of_performers')}>
            <label htmlFor="number_of_performers">Number of Performers</label>
            <input
              type="number"
              id="number_of_performers"
              name="number_of_performers"
              value={formData.number_of_performers}
              onChange={handleChange}
              required
              placeholder="e.g., 5"
              min="1"
            />
            {formErrors.number_of_performers && <span className="error-message">{formErrors.number_of_performers}</span>}
          </div>

          <div className={getFieldClassName('performer_names')}>
            <label htmlFor="performer_names">Performer Names (comma-separated)</label>
            <textarea
              id="performer_names"
              name="performer_names"
              value={formData.performer_names}
              onChange={handleChange}
              required
              placeholder="e.g., John Doe, Jane Smith, Alex Lee"
              rows="3"
            />
            {formErrors.performer_names && <span className="error-message">{formErrors.performer_names}</span>}
          </div>

          <div className={getFieldClassName('group')}>
            <label htmlFor="group">Group</label>
            <select
              id="group"
              name="group"
              value={formData.group}
              onChange={handleChange}
              required
            >
              <option value="">Select a group</option>
              <option value="Group A">Group A üé™</option>
              <option value="Group B">Group B üé≠</option>
              <option value="Group C">Group C üé®</option>
              <option value="Group D">Group D ‚ú®</option>
              <option value="Individual">Individual üåü</option>
            </select>
            {formErrors.group && <span className="error-message">{formErrors.group}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="performance_description">Performance Description (Optional)</label>
            <div className="description-container">
              <textarea
                id="performance_description"
                name="performance_description"
                value={formData.performance_description}
                onChange={handleChange}
                placeholder="A short, engaging description of your performance."
                rows="3"
              />
              <button 
                type="button" 
                className="generate-btn"
                onClick={handleGenerateDescription}
              >
                ‚ú® Generate Description
              </button>
            </div>
          </div>
        </div>

        <div className="form-section">
          <h2>Coordinator Information</h2>

          <div className={getFieldClassName('coordinator_name')}>
            <label htmlFor="coordinator_name">Coordinator Name</label>
            <input
              type="text"
              id="coordinator_name"
              name="coordinator_name"
              value={formData.coordinator_name}
              onChange={handleChange}
              required
              placeholder="e.g., Priya Sharma"
            />
            {formErrors.coordinator_name && <span className="error-message">{formErrors.coordinator_name}</span>}
          </div>

          <div className={getFieldClassName('contact_number')}>
            <label htmlFor="contact_number">Contact Number</label>
            <input
              type="tel"
              id="contact_number"
              name="contact_number"
              value={formData.contact_number}
              onChange={handleChange}
              required
              placeholder="e.g., 1234567890"
            />
            {formErrors.contact_number && <span className="error-message">{formErrors.contact_number}</span>}
          </div>
        </div>

        <div className="form-actions">
          <button
            type="submit"
            disabled={isSubmitting || !isValid}
            className={`submit-button ${isValid ? 'valid' : 'invalid'}`}
          >
            {isSubmitting ? 'Submitting... üöÄ' : 'Submit Signup ‚ú®'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EventSignupForm;
