import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper,
  Grid,
  IconButton,
  Badge,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Chip,
  CircularProgress,
  ToggleButton,
  ToggleButtonGroup,
  Divider
} from '@mui/material';
import { 
  format, 
  startOfMonth, 
  endOfMonth, 
  eachDayOfInterval,
  isSameMonth,
  isSameDay,
  addMonths,
  subMonths,
  startOfWeek,
  endOfWeek,
  eachHourOfInterval,
  startOfDay,
  endOfDay,
  addDays,
  parseISO,
  isWithinInterval
} from 'date-fns';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import CalendarViewMonthIcon from '@mui/icons-material/CalendarViewMonth';
import CalendarViewDayIcon from '@mui/icons-material/CalendarViewDay';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

// Add these new imports at the top
import { 
  Tooltip,
  Zoom,
  Button,
  Menu,
  MenuItem,
  Fade,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import EventIcon from '@mui/icons-material/Event';
import TodayIcon from '@mui/icons-material/Today';

function Calendar() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [view, setView] = useState('month');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tasks`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTasks(response.data);
    } catch (err) {
      console.error('Failed to fetch tasks:', err);
    } finally {
      setLoading(false);
    }
  };

  const getDaysInMonth = () => {
    const start = startOfMonth(currentDate);
    const end = endOfMonth(currentDate);
    return eachDayOfInterval({ start, end });
  };

  const getTasksForDate = (date) => {
    return tasks.filter(task => 
      task.due_date && isSameDay(new Date(task.due_date), date)
    );
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setDrawerOpen(true);
  };

  const priorityColors = {
    high: '#DC143C',
    medium: '#FFA500',
    low: '#4CAF50'
  };

  const handleViewChange = (event, newView) => {
    if (newView !== null) {
      setView(newView);
    }
  };

  const getTasksForHour = (date, hour) => {
    return tasks.filter(task => {
      if (!task.due_date) return false;
      const taskDate = parseISO(task.due_date);
      return isSameDay(taskDate, date) && taskDate.getHours() === hour;
    });
  };

  const renderDayView = () => {
    const hours = eachHourOfInterval({
      start: startOfDay(selectedDate || new Date()),
      end: endOfDay(selectedDate || new Date())
    });

    return (
      <Box sx={{ mt: 3 }}>
        {hours.map((hour) => {
          const tasksForHour = getTasksForHour(selectedDate || new Date(), hour.getHours());
          return (
            <Paper
              key={hour.toString()}
              sx={{
                mb: 2,
                p: 2,
                background: 'rgba(255, 255, 255, 0.03)',
                borderRadius: 2,
                border: '1px solid rgba(255, 255, 255, 0.1)',
              }}
            >
              <Typography sx={{ color: '#fff', mb: 1 }}>
                {format(hour, 'h:00 a')}
              </Typography>
              {tasksForHour.length > 0 ? (
                tasksForHour.map(task => (
                  <Box
                    key={task.id}
                    sx={{
                      p: 1.5,
                      mt: 1,
                      background: 'rgba(255, 255, 255, 0.05)',
                      borderRadius: 1,
                      border: '1px solid rgba(255, 255, 255, 0.1)',
                    }}
                  >
                    <Typography sx={{ color: '#fff' }}>{task.title}</Typography>
                    <Chip
                      label={task.priority}
                      size="small"
                      sx={{
                        mt: 1,
                        backgroundColor: `${priorityColors[task.priority]}15`,
                        color: priorityColors[task.priority],
                      }}
                    />
                  </Box>
                ))
              ) : (
                <Typography sx={{ color: 'rgba(255, 255, 255, 0.3)', fontSize: '0.9rem' }}>
                  No tasks scheduled
                </Typography>
              )}
            </Paper>
          );
        })}
      </Box>
    );
  };

  // Add these new state variables
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [taskDialogOpen, setTaskDialogOpen] = useState(false);

  // Add this new function for task details dialog
  const handleTaskClick = (task) => {
    setSelectedTask(task);
    setTaskDialogOpen(true);
  };

  const renderMonthView = () => {
    const start = startOfWeek(startOfMonth(currentDate));
    const end = endOfWeek(endOfMonth(currentDate));
    const days = eachDayOfInterval({ start, end });

    return (
      <Box sx={{ 
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: 2,
        overflow: 'hidden',
      }}>
        <Grid container sx={{ borderBottom: '1px solid rgba(255, 255, 255, 0.1)' }}>
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
            <Grid item xs key={day}>
              <Box sx={{
                py: 2,
                textAlign: 'center',
                borderRight: '1px solid rgba(255, 255, 255, 0.1)',
                '&:last-child': { borderRight: 'none' },
              }}>
                <Typography sx={{ 
                  color: day === 'Sun' ? '#DC143C' : 'rgba(255, 255, 255, 0.7)',
                  fontSize: '0.85rem',
                  fontWeight: 600,
                  letterSpacing: '1px',
                }}>
                  {day}
                </Typography>
              </Box>
            </Grid>
          ))}
        </Grid>

        <Grid container>
          {days.map((date) => {
            const tasksForDate = getTasksForDate(date);
            const isToday = isSameDay(date, new Date());
            const isSelected = selectedDate && isSameDay(date, selectedDate);

            return (
              <Grid item xs key={date.toString()} sx={{
                borderRight: '1px solid rgba(255, 255, 255, 0.1)',
                borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
                '&:nth-of-type(7n)': { borderRight: 'none' },
                '&:nth-last-of-type(-n+7)': { borderBottom: 'none' },
              }}>
                <Box
                  onClick={() => {
                    setSelectedDate(date);
                    setView('day');
                  }}
                  sx={{
                    minHeight: 120,
                    p: 1,
                    cursor: 'pointer',
                    position: 'relative',
                    background: isSelected 
                      ? 'rgba(220, 20, 60, 0.15)'
                      : isToday
                      ? 'rgba(255, 255, 255, 0.05)'
                      : 'transparent',
                    '&:hover': {
                      background: 'rgba(255, 255, 255, 0.08)',
                    },
                    transition: 'all 0.2s ease-in-out',
                  }}
                >
                  <Box sx={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mb: 1,
                  }}>
                    <Typography
                      sx={{
                        color: isSameMonth(date, currentDate)
                          ? isToday
                            ? '#DC143C'
                            : '#fff'
                          : 'rgba(255, 255, 255, 0.3)',
                        fontSize: '1.1rem',
                        fontWeight: isToday ? 600 : 400,
                        position: 'relative',
                        width: 'fit-content',
                      }}
                    >
                      {format(date, 'd')}
                      {isToday && (
                        <Box
                          sx={{
                            position: 'absolute',
                            bottom: -4,
                            left: '50%',
                            transform: 'translateX(-50%)',
                            width: 4,
                            height: 4,
                            borderRadius: '50%',
                            backgroundColor: '#DC143C',
                          }}
                        />
                      )}
                    </Typography>
                  </Box>

                  <Box sx={{ mt: 1 }}>
                    {tasksForDate.slice(0, 3).map((task) => (
                      <Tooltip
                        key={task.id}
                        title={task.title}
                        TransitionComponent={Zoom}
                        arrow
                      >
                        <Box
                          onClick={(e) => {
                            e.stopPropagation();
                            handleTaskClick(task);
                          }}
                          sx={{
                            p: 0.75,
                            mb: 0.5,
                            borderRadius: 1,
                            backgroundColor: `${priorityColors[task.priority]}15`,
                            border: `1px solid ${priorityColors[task.priority]}30`,
                            cursor: 'pointer',
                            '&:hover': {
                              backgroundColor: `${priorityColors[task.priority]}25`,
                            },
                          }}
                        >
                          <Typography
                            noWrap
                            sx={{
                              fontSize: '0.75rem',
                              color: '#fff',
                            }}
                          >
                            {task.title}
                          </Typography>
                        </Box>
                      </Tooltip>
                    ))}
                    {tasksForDate.length > 3 && (
                      <Tooltip
                        title={`${tasksForDate.length - 3} more tasks`}
                        arrow
                      >
                        <Typography 
                          sx={{ 
                            color: 'rgba(255, 255, 255, 0.7)',
                            fontSize: '0.75rem',
                            textAlign: 'center',
                            mt: 0.5,
                            cursor: 'pointer',
                          }}
                        >
                          +{tasksForDate.length - 3} more
                        </Typography>
                      </Tooltip>
                    )}
                  </Box>
                </Box>
              </Grid>
            );
          })}
        </Grid>
      </Box>
    );
  };

  // Add this new dialog component at the end of the return statement
  return (
    <>
      <Dialog
        open={taskDialogOpen}
        onClose={() => setTaskDialogOpen(false)}
        TransitionComponent={Fade}
        PaperProps={{
          sx: {
            background: 'rgba(17, 17, 17, 0.95)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            minWidth: 400,
          }
        }}
      >
        {selectedTask && (
          <>
            <DialogTitle sx={{ 
              color: '#fff',
              borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
            }}>
              {selectedTask.title}
            </DialogTitle>
            <DialogContent sx={{ mt: 2 }}>
              <Typography sx={{ color: 'rgba(255, 255, 255, 0.7)', mb: 2 }}>
                {selectedTask.description || 'No description provided'}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                <Chip
                  label={selectedTask.priority}
                  size="small"
                  sx={{
                    backgroundColor: `${priorityColors[selectedTask.priority]}15`,
                    color: priorityColors[selectedTask.priority],
                  }}
                />
                {selectedTask.estimated_time && (
                  <Chip
                    label={`${selectedTask.estimated_time}h`}
                    size="small"
                    sx={{
                      backgroundColor: 'rgba(255, 255, 255, 0.1)',
                      color: '#fff',
                    }}
                  />
                )}
              </Box>
              <Typography sx={{ color: 'rgba(255, 255, 255, 0.5)', fontSize: '0.9rem' }}>
                Due: {format(new Date(selectedTask.due_date), 'PPp')}
              </Typography>
            </DialogContent>
            <DialogActions sx={{ borderTop: '1px solid rgba(255, 255, 255, 0.1)' }}>
              <Button 
                onClick={() => setTaskDialogOpen(false)}
                sx={{ color: 'rgba(255, 255, 255, 0.7)' }}
              >
                Close
              </Button>
            </DialogActions>
          </>
        )}
      </Dialog>
    </>
  );
}

export default Calendar;
