import React, { useState, useEffect } from 'react';
import {
    Box,
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Chip,
    IconButton,
    Alert,
    CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

const priorities = [
    { value: 'high', label: 'High', color: '#DC143C' },
    { value: 'medium', label: 'Medium', color: '#FFA500' },
    { value: 'low', label: 'Low', color: '#4CAF50' }
];

function TaskScheduler() {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        subject: '',
        priority: 'medium',
        estimatedTime: ''
    });

    // Fetch tasks on component mount
    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_URL}/tasks`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });
            setTasks(response.data);
        } catch (err) {
            setError('Failed to fetch tasks');
            console.error('Error fetching tasks:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setLoading(true);

        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_URL}/tasks`,
                formData,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            setTasks([...tasks, response.data]);
            setSuccess('Task added successfully!');
            setFormData({
                title: '',
                description: '',
                subject: '',
                priority: 'medium',
                estimatedTime: ''
            });
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add task');
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (taskId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`${API_URL}/tasks/${taskId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setTasks(tasks.filter(task => task.id !== taskId));
            setSuccess('Task deleted successfully!');
        } catch (err) {
            setError('Failed to delete task');
        }
    };

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4
            }}
        >
            <Container maxWidth="lg">
                <Grid container spacing={4}>
                    {/* Task Creation Form */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={0}
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                            }}
                        >
                            <Typography variant="h5" sx={{ mb: 3, color: '#fff' }}>
                                Create New Task
                            </Typography>

                            {error && (
                                <Alert severity="error" sx={{ mb: 2 }}>
                                    {error}
                                </Alert>
                            )}

                            {success && (
                                <Alert severity="success" sx={{ mb: 2 }}>
                                    {success}
                                </Alert>
                            )}

                            <form onSubmit={handleSubmit}>
                                <Grid container spacing={2}>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Task Title"
                                            name="title"
                                            value={formData.title}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                title: e.target.value
                                            })}
                                            required
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Subject"
                                            name="subject"
                                            value={formData.subject}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                subject: e.target.value
                                            })}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            multiline
                                            rows={3}
                                            label="Description"
                                            name="description"
                                            value={formData.description}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                description: e.target.value
                                            })}
                                        />
                                    </Grid>

                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            fullWidth
                                            select
                                            label="Priority"
                                            name="priority"
                                            value={formData.priority}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                priority: e.target.value
                                            })}
                                        >
                                            {priorities.map((priority) => (
                                                <MenuItem key={priority.value} value={priority.value}>
                                                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                                        <Box
                                                            sx={{
                                                                width: 12,
                                                                height: 12,
                                                                borderRadius: '50%',
                                                                backgroundColor: priority.color,
                                                                mr: 1
                                                            }}
                                                        />
                                                        {priority.label}
                                                    </Box>
                                                </MenuItem>
                                            ))}
                                        </TextField>
                                    </Grid>

                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            fullWidth
                                            type="number"
                                            label="Estimated Time (hours)"
                                            name="estimatedTime"
                                            value={formData.estimatedTime}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                estimatedTime: e.target.value
                                            })}
                                            InputProps={{
                                                inputProps: { min: 0, step: 0.5 }
                                            }}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <Button
                                            type="submit"
                                            variant="contained"
                                            fullWidth
                                            disabled={loading}
                                            sx={{
                                                mt: 2,
                                                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                                                '&:hover': {
                                                    background: 'linear-gradient(45deg, #FF4D6A, #DC143C)',
                                                }
                                            }}
                                        >
                                            {loading ? <CircularProgress size={24} /> : 'Create Task'}
                                        </Button>
                                    </Grid>
                                </Grid>
                            </form>
                        </Paper>
                    </Grid>

                    {/* Task List */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={0}
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                            }}
                        >
                            <Typography variant="h5" sx={{ mb: 3, color: '#fff' }}>
                                Your Tasks
                            </Typography>

                            {loading ? (
                                <Box sx={{ display: 'flex', justifyContent: 'center', py: 3 }}>
                                    <CircularProgress />
                                </Box>
                            ) : tasks.length === 0 ? (
                                <Typography color="text.secondary" align="center">
                                    No tasks yet. Create your first task!
                                </Typography>
                            ) : (
                                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                                    {tasks.map((task) => (
                                        <Paper
                                            key={task.id}
                                            sx={{
                                                p: 2,
                                                background: 'rgba(255, 255, 255, 0.05)',
                                                borderRadius: 2,
                                                transition: 'transform 0.2s ease-in-out',
                                                '&:hover': {
                                                    transform: 'translateY(-2px)',
                                                }
                                            }}
                                        >
                                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 1 }}>
                                                <Typography variant="h6" sx={{ color: '#fff' }}>
                                                    {task.title}
                                                </Typography>
                                                <Box>
                                                    <IconButton
                                                        size="small"
                                                        onClick={() => handleDelete(task.id)}
                                                        sx={{ color: 'rgba(255, 255, 255, 0.5)' }}
                                                    >
                                                        <DeleteIcon />
                                                    </IconButton>
                                                </Box>
                                            </Box>

                                            {task.subject && (
                                                <Typography variant="body2" color="text.secondary" gutterBottom>
                                                    {task.subject}
                                                </Typography>
                                            )}

                                            {task.description && (
                                                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                                                    {task.description}
                                                </Typography>
                                            )}

                                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                                <Chip
                                                    label={task.priority}
                                                    size="small"
                                                    sx={{
                                                        backgroundColor: `${priorities.find(p => p.value === task.priority)?.color}20`,
                                                        color: priorities.find(p => p.value === task.priority)?.color,
                                                        fontWeight: 500,
                                                        textTransform: 'capitalize'
                                                    }}
                                                />
                                                {task.estimatedTime && (
                                                    <Typography variant="body2" color="text.secondary">
                                                        {task.estimatedTime} hours
                                                    </Typography>
                                                )}
                                            </Box>
                                        </Paper>
                                    ))}
                                </Box>
                            )}
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default TaskScheduler;
