import React, { useState } from 'react';
import {
    Box,
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Chip,
    IconButton,
    // eslint-disable-next-line no-unused-vars
    Divider,
    Alert
} from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import PriorityHighIcon from '@mui/icons-material/PriorityHigh';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
// eslint-disable-next-line no-unused-vars
import axios from 'axios';

// eslint-disable-next-line no-unused-vars
const API_URL = 'http://127.0.0.1:5000';

const priorities = [
    { value: 'high', label: 'High', color: '#DC143C' },
    { value: 'medium', label: 'Medium', color: '#FFA500' },
    { value: 'low', label: 'Low', color: '#4CAF50' }
];

// Task Display Component
const TaskDisplay = ({ task, onDelete }) => {
    const getPriorityColor = (priority) => {
        switch (priority) {
            case 'high':
                return '#DC143C';
            case 'medium':
                return '#FFA500';
            case 'low':
                return '#4CAF50';
            default:
                return '#757575';
        }
    };

    return (
        <Box
            sx={{
                position: 'relative',
                '&:hover .delete-button': {
                    opacity: 1,
                }
            }}
        >
            <Paper
                elevation={0}
                sx={{
                    p: 2,
                    background: 'rgba(255, 255, 255, 0.05)',
                    borderRadius: 2,
                    transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
                    '&:hover': {
                        transform: 'translateY(-2px)',
                        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                    }
                }}
            >
                <Box sx={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'space-between', mb: 1 }}>
                    <Typography variant="h6" sx={{ color: '#fff', fontWeight: 500 }}>
                        {task.title}
                    </Typography>
                    <IconButton 
                        className="delete-button"
                        onClick={() => onDelete(task.id)}
                        sx={{ 
                            opacity: 0,
                            transition: 'opacity 0.2s ease-in-out',
                            color: 'rgba(255, 255, 255, 0.5)',
                            '&:hover': {
                                color: '#DC143C'
                            }
                        }}
                    >
                        <DeleteOutlineIcon />
                    </IconButton>
                </Box>

                {task.subject && (
                    <Chip
                        label={task.subject}
                        size="small"
                        sx={{
                            mb: 1,
                            background: 'rgba(220, 20, 60, 0.1)',
                            color: 'rgba(255, 255, 255, 0.7)',
                            borderRadius: '8px',
                        }}
                    />
                )}

                {task.description && (
                    <Typography 
                        variant="body2" 
                        sx={{ 
                            color: 'rgba(255, 255, 255, 0.7)',
                            mb: 2
                        }}
                    >
                        {task.description}
                    </Typography>
                )}

                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <AccessTimeIcon sx={{ fontSize: 20, color: 'rgba(255, 255, 255, 0.5)' }} />
                            <Typography variant="body2" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
                                {task.estimatedTime} hours estimated
                            </Typography>
                        </Box>
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <PriorityHighIcon 
                                sx={{ 
                                    fontSize: 20, 
                                    color: getPriorityColor(task.priority)
                                }} 
                            />
                            <Typography 
                                variant="body2" 
                                sx={{ 
                                    color: getPriorityColor(task.priority),
                                    textTransform: 'capitalize'
                                }}
                            >
                                {task.priority} Priority
                            </Typography>
                        </Box>
                    </Grid>
                </Grid>
            </Paper>
        </Box>
    );
};

// Main Component
function TaskScheduler() {
    const [taskList, setTaskList] = useState([]);
    const [task, setTask] = useState({
        title: '',
        description: '',
        estimatedTime: '',
        priority: 'medium',
        subject: ''
    });

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        // Temporary task creation for testing
        const newTask = {
            id: Date.now(), // temporary ID
            ...task
        };

        setTaskList([...taskList, newTask]);
        setSuccess('Task added successfully!');
        setTask({
            title: '',
            description: '',
            estimatedTime: '',
            priority: 'medium',
            subject: ''
        });
    };

    const handleDelete = (taskId) => {
        setTaskList(taskList.filter(t => t.id !== taskId));
    };

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4
            }}
        >
            <Container maxWidth="lg">
                <Grid container spacing={4}>
                    {/* Task Creation Form */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={0}
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                            }}
                        >
                            <Typography variant="h5" sx={{ mb: 3, color: '#fff' }}>
                                Schedule New Task
                            </Typography>

                            {error && (
                                <Alert severity="error" sx={{ mb: 2 }}>
                                    {error}
                                </Alert>
                            )}

                            {success && (
                                <Alert severity="success" sx={{ mb: 2 }}>
                                    {success}
                                </Alert>
                            )}

                            <form onSubmit={handleSubmit}>
                                <Grid container spacing={2}>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Task Title"
                                            value={task.title}
                                            onChange={(e) => setTask({ ...task, title: e.target.value })}
                                            required
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Subject"
                                            value={task.subject}
                                            onChange={(e) => setTask({ ...task, subject: e.target.value })}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            multiline
                                            rows={3}
                                            label="Description"
                                            value={task.description}
                                            onChange={(e) => setTask({ ...task, description: e.target.value })}
                                        />
                                    </Grid>

                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            fullWidth
                                            label="Estimated Time (hours)"
                                            type="number"
                                            value={task.estimatedTime}
                                            onChange={(e) => setTask({ ...task, estimatedTime: e.target.value })}
                                            InputProps={{
                                                inputProps: { min: 0, step: 0.5 }
                                            }}
                                            required
                                        />
                                    </Grid>

                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            fullWidth
                                            select
                                            label="Priority"
                                            value={task.priority}
                                            onChange={(e) => setTask({ ...task, priority: e.target.value })}
                                            required
                                        >
                                            {priorities.map((priority) => (
                                                <MenuItem key={priority.value} value={priority.value}>
                                                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                                        <Box
                                                            sx={{
                                                                width: 12,
                                                                height: 12,
                                                                borderRadius: '50%',
                                                                backgroundColor: priority.color,
                                                                mr: 1
                                                            }}
                                                        />
                                                        {priority.label}
                                                    </Box>
                                                </MenuItem>
                                            ))}
                                        </TextField>
                                    </Grid>

                                    <Grid item xs={12}>
                                        <Button
                                            type="submit"
                                            variant="contained"
                                            fullWidth
                                            sx={{
                                                mt: 2,
                                                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                                                '&:hover': {
                                                    background: 'linear-gradient(45deg, #FF4D6A, #DC143C)',
                                                }
                                            }}
                                        >
                                            Schedule Task
                                        </Button>
                                    </Grid>
                                </Grid>
                            </form>
                        </Paper>
                    </Grid>

                    {/* Task List */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={0}
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                            }}
                        >
                            <Typography variant="h5" sx={{ mb: 3, color: '#fff' }}>
                                Scheduled Tasks
                            </Typography>

                            {taskList.length === 0 ? (
                                <Typography color="text.secondary" align="center">
                                    No tasks scheduled yet
                                </Typography>
                            ) : (
                                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                                    {taskList.map((t) => (
                                        <TaskDisplay
                                            key={t.id}
                                            task={t}
                                            onDelete={handleDelete}
                                        />
                                    ))}
                                </Box>
                            )}
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default TaskScheduler;
