import React, { useState, useEffect } from 'react';
import {
    Box,
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Chip,
    IconButton,
    Alert,
    CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

const priorities = [
    { value: 'high', label: 'High', color: '#DC143C' },
    { value: 'medium', label: 'Medium', color: '#FFA500' },
    { value: 'low', label: 'Low', color: '#4CAF50' }
];

function TaskScheduler() {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        subject: '',
        priority: 'medium',
        estimatedTime: ''
    });

    // Fetch tasks on component mount
    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            console.log('Using token:', token); // Debug log

            const response = await axios.get(`${API_URL}/tasks`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            console.log('Response:', response.data); // Debug log
            setTasks(response.data);
        } catch (err) {
            console.error('Full error:', err); // More detailed error logging
            setError(err.response?.data?.error || 'Failed to fetch tasks');
        } finally {
            setLoading(false);
        }
    };    

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setLoading(true);

        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_URL}/tasks`,
                formData,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            setTasks([...tasks, response.data]);
            setSuccess('Task added successfully!');
            setFormData({
                title: '',
                description: '',
                subject: '',
                priority: 'medium',
                estimatedTime: ''
            });
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add task');
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (taskId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`${API_URL}/tasks/${taskId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setTasks(tasks.filter(task => task.id !== taskId));
            setSuccess('Task deleted successfully!');
        } catch (err) {
            setError('Failed to delete task');
        }
    };

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4,
                px: 3
            }}
        >
            <Container maxWidth="xl">
                <Grid container spacing={4}>
                    {/* Left Panel - Task Stats & Creation */}
                    <Grid item xs={12} md={4}>
                        {/* Quick Stats */}
                        <Box sx={{ mb: 4 }}>
                            <Grid container spacing={2}>
                                <Grid item xs={6}>
                                    <Paper
                                        sx={{
                                            p: 2,
                                            background: 'rgba(220, 20, 60, 0.1)',
                                            borderRadius: 3,
                                            border: '1px solid rgba(220, 20, 60, 0.2)',
                                        }}
                                    >
                                        <Typography variant="h3" sx={{ color: '#DC143C', fontWeight: 600 }}>
                                            {tasks.length}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                                            Total Tasks
                                        </Typography>
                                    </Paper>
                                </Grid>
                                <Grid item xs={6}>
                                    <Paper
                                        sx={{
                                            p: 2,
                                            background: 'rgba(255, 255, 255, 0.03)',
                                            borderRadius: 3,
                                            border: '1px solid rgba(255, 255, 255, 0.1)',
                                        }}
                                    >
                                        <Typography variant="h3" sx={{ color: '#fff', fontWeight: 600 }}>
                                            {tasks.filter(t => t.priority === 'high').length}
                                        </Typography>
                                        <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                                            High Priority
                                        </Typography>
                                    </Paper>
                                </Grid>
                            </Grid>
                        </Box>

                        {/* Task Creation Form */}
                        <Paper
                            elevation={0}
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                position: 'sticky',
                                top: 24
                            }}
                        >
                            <Typography variant="h5" sx={{ mb: 3, color: '#fff' }}>
                                Create New Task
                            </Typography>

                            {error && (
                                <Alert severity="error" sx={{ mb: 2 }}>
                                    {error}
                                </Alert>
                            )}

                            {success && (
                                <Alert severity="success" sx={{ mb: 2 }}>
                                    {success}
                                </Alert>
                            )}

                            <form onSubmit={handleSubmit}>
                                <Grid container spacing={2}>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Task Title"
                                            name="title"
                                            value={formData.title}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                title: e.target.value
                                            })}
                                            required
                                            sx={textFieldStyle}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Subject"
                                            name="subject"
                                            value={formData.subject}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                subject: e.target.value
                                            })}
                                            sx={textFieldStyle}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            multiline
                                            rows={3}
                                            label="Description"
                                            name="description"
                                            value={formData.description}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                description: e.target.value
                                            })}
                                            sx={textFieldStyle}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            select
                                            label="Priority"
                                            name="priority"
                                            value={formData.priority}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                priority: e.target.value
                                            })}
                                            sx={textFieldStyle}
                                        >
                                            {priorities.map((priority) => (
                                                <MenuItem key={priority.value} value={priority.value}>
                                                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                                                        <Box
                                                            sx={{
                                                                width: 12,
                                                                height: 12,
                                                                borderRadius: '50%',
                                                                backgroundColor: priority.color,
                                                                mr: 1
                                                            }}
                                                        />
                                                        {priority.label}
                                                    </Box>
                                                </MenuItem>
                                            ))}
                                        </TextField>
                                    </Grid>

                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            type="number"
                                            label="Estimated Time (hours)"
                                            name="estimatedTime"
                                            value={formData.estimatedTime}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                estimatedTime: e.target.value
                                            })}
                                            InputProps={{
                                                inputProps: { min: 0, step: 0.5 }
                                            }}
                                            sx={textFieldStyle}
                                        />
                                    </Grid>

                                    <Grid item xs={12}>
                                        <Button
                                            type="submit"
                                            variant="contained"
                                            fullWidth
                                            disabled={loading}
                                            sx={{
                                                py: 1.5,
                                                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                                                borderRadius: 2,
                                                textTransform: 'none',
                                                fontSize: '1.1rem',
                                                fontWeight: 500,
                                                boxShadow: '0 8px 16px rgba(220, 20, 60, 0.2)',
                                                '&:hover': {
                                                    background: 'linear-gradient(45deg, #FF4D6A, #DC143C)',
                                                    transform: 'translateY(-2px)',
                                                    boxShadow: '0 12px 20px rgba(220, 20, 60, 0.3)',
                                                },
                                                transition: 'all 0.3s ease'
                                            }}
                                        >
                                            {loading ? <CircularProgress size={24} /> : 'Create Task'}
                                        </Button>
                                    </Grid>
                                </Grid>
                            </form>
                        </Paper>
                    </Grid>

                    {/* Right Panel - Task List */}
