import React from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Button, 
  Grid,
  useTheme,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import AutoGraphIcon from '@mui/icons-material/AutoGraph';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

function Home() {
  const theme = useTheme();
  const navigate = useNavigate();

  return (
    <Box sx={{ background: '#000000' }}>
      {/* Hero Section */}
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
          position: 'relative',
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: '-20%',
            right: '-10%',
            width: '60%',
            height: '60%',
            background: 'radial-gradient(circle, rgba(220, 20, 60, 0.15) 0%, transparent 70%)',
            filter: 'blur(80px)',
            zIndex: 1,
          }
        }}
      >
        <Container maxWidth="lg">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <Grid container spacing={4} alignItems="center">
              <Grid item xs={12} md={8}>
                <Typography
                  component="h1"
                  sx={{
                    fontSize: { xs: '3rem', md: '5rem', lg: '6rem' },
                    fontWeight: 700,
                    lineHeight: 1.1,
                    mb: 3,
                    background: 'linear-gradient(45deg, #fff 30%, #DC143C 90%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    letterSpacing: '-0.02em',
                  }}
                >
                  Master Your Time with AI
                </Typography>
                <Typography
                  sx={{
                    fontSize: { xs: '1.25rem', md: '1.5rem' },
                    color: 'rgba(255, 255, 255, 0.7)',
                    mb: 4,
                    maxWidth: '600px',
                    lineHeight: 1.6,
                  }}
                >
                  Transform your study routine with AI-powered scheduling that adapts to your unique learning style.
                </Typography>
                <Box sx={{ display: 'flex', gap: 3 }}>
                  <Button
                    variant="contained"
                    onClick={() => navigate('/register')}
                    sx={{
                      py: 2,
                      px: 4,
                      fontSize: '1.1rem',
                      textTransform: 'none',
                      borderRadius: '12px',
                      background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                      boxShadow: '0 4px 15px rgba(220, 20, 60, 0.3)',
                      '&:hover': {
                        transform: 'translateY(-2px)',
                        boxShadow: '0 6px 20px rgba(220, 20, 60, 0.4)',
                      },
                    }}
                  >
                    Get Started Free
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={() => navigate('/login')}
                    sx={{
                      py: 2,
                      px: 4,
                      fontSize: '1.1rem',
                      textTransform: 'none',
                      borderRadius: '12px',
                      borderColor: 'rgba(255, 255, 255, 0.1)',
                      color: 'white',
                      '&:hover': {
                        borderColor: '#DC143C',
                        backgroundColor: 'rgba(220, 20, 60, 0.1)',
                      },
                    }}
                  >
                    Login
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </motion.div>
        </Container>
      </Box>

      {/* Features Section */}
      <Box sx={{ py: { xs: 12, md: 20 } }}>
        <Container maxWidth="lg">
          <Typography
            variant="h2"
            sx={{
              textAlign: 'center',
              mb: 8,
              fontSize: { xs: '2.5rem', md: '3.5rem' },
              fontWeight: 700,
              background: 'linear-gradient(45deg, #fff 30%, #DC143C 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}
          >
            Powerful Features
          </Typography>
          <Grid container spacing={8}>
            {[
              {
                  icon: <AccessTimeIcon sx={{ fontSize: 32, color: '#DC143C' }} />,
                  title: "Smart Scheduling",
                  description: "AI-powered scheduling that adapts to your study habits and preferences.",
                },
                {
                  icon: <AutoGraphIcon sx={{ fontSize: 32, color: '#DC143C' }} />,
                  title: "Performance Analytics",
                  description: "Track your progress and optimize your study efficiency with real-time insights.",
                },
                {
                  icon: <CalendarMonthIcon sx={{ fontSize: 32, color: '#DC143C' }} />,
                  title: "Seamless Planning",
                  description: "Effortlessly manage your academic schedule with our intuitive interface.",
                },
              ].map((feature, index) => (
                <Grid item xs={12} md={4} key={index}>
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.2 }}
                    viewport={{ once: true }}
                  >
                    <Box
                      sx={{
                        p: 6,
                        height: '100%',
                        background: 'rgba(255, 255, 255, 0.02)',
                        borderRadius: '30px',
                        border: '1px solid rgba(255, 255, 255, 0.05)',
                        transition: 'all 0.4s ease',
                        '&:hover': {
                          transform: 'translateY(-12px)',
                          background: 'rgba(255, 255, 255, 0.03)',
                          borderColor: 'rgba(220, 20, 60, 0.2)',
                          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.4)',
                        },
                      }}
                    >
                      <Box
                        sx={{
                          display: 'inline-flex',
                          p: 3,
                          borderRadius: '20px',
                          background: 'rgba(220, 20, 60, 0.1)',
                          mb: 4,
                        }}
                      >
                        {React.cloneElement(feature.icon, { 
                          sx: { fontSize: 48, color: '#DC143C' }
                        })}
                      </Box>
                      <Typography
                        variant="h4"
                        sx={{
                          mb: 3,
                          color: '#fff',
                          fontWeight: 600,
                          fontSize: { xs: '1.75rem', md: '2rem' },
                        }}
                      >
                        {feature.title}
                      </Typography>
                      <Typography
                        sx={{
                          color: 'rgba(255, 255, 255, 0.7)',
                          lineHeight: 1.8,
                          fontSize: '1.1rem',
                        }}
                      >
                        {feature.description}
                      </Typography>
                    </Box>
                  </motion.div>
                </Grid>
              ))}
            </Grid>
          </Container>
        </Box>

      {/* CTA Section */}
      <Box
        sx={{
          py: { xs: 12, md: 16 },
          position: 'relative',
          '&::before': {
            content: '""',
            position: 'absolute',
            bottom: 0,
            left: 0,
            width: '50%',
            height: '50%',
            background: 'radial-gradient(circle, rgba(220, 20, 60, 0.15) 0%, transparent 70%)',
            filter: 'blur(80px)',
          }
        }}
      >
        <Container maxWidth="md">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <Box sx={{ textAlign: 'center' }}>
              <Typography
                variant="h2"
                sx={{
                  mb: 3,
                  fontSize: { xs: '2.5rem', md: '3.5rem' },
                  fontWeight: 700,
                  color: '#fff',
                  letterSpacing: '-0.02em',
                }}
              >
                Ready to Transform Your Study Routine?
              </Typography>
              <Typography
                sx={{
                  mb: 4,
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontSize: { xs: '1.1rem', md: '1.25rem' },
                  maxWidth: '600px',
                  mx: 'auto',
                }}
              >
                Join thousands of students who are maximizing their potential with Tempo.
              </Typography>
              <Button
                variant="contained"
                endIcon={<ArrowForwardIcon />}
                onClick={() => navigate('/register')}
                sx={{
                  py: 2,
                  px: 6,
                  fontSize: '1.1rem',
                  textTransform: 'none',
                  borderRadius: '12px',
                  background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                  boxShadow: '0 4px 15px rgba(220, 20, 60, 0.3)',
                  '&:hover': {
                    transform: 'translateY(-2px)',
                    boxShadow: '0 6px 20px rgba(220, 20, 60, 0.4)',
                  },
                }}
              >
                Get Started Now
              </Button>
            </Box>
          </motion.div>
        </Container>
      </Box>
    </Box>
  );
}

export default Home;
