import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Button,
  Typography,
  Box,
  Link,
  Alert,
  InputAdornment,
  Divider,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import { motion } from 'framer-motion';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext'; // Add this import
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import ChatIcon from '@mui/icons-material/Chat';
import DirectionsRunIcon from '@mui/icons-material/DirectionsRun';

const API_URL = 'http://127.0.0.1:5000';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();
  const { login, logout } = useAuth(); // Add this

  // Clear authentication when component mounts
  useEffect(() => {
    logout();
  }, [logout]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post(
        `${API_URL}/login`,
        {
          email: formData.email,
          password: formData.password
        },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.access_token) {
        login(response.data.access_token); // Use context login instead of localStorage
        setSuccess('Login successful! Redirecting...');

        // Clear form
        setFormData({
          email: '',
          password: ''
        });

        // Redirect to dashboard
        setTimeout(() => {
          navigate('/dashboard');
        }, 1500);
      }
    } catch (err) {
      console.error('Login error:', err);
      if (!err.response) {
        setError('Unable to connect to the server. Please try again later.');
      } else {
        setError(err.response?.data?.error || 'Invalid credentials. Please try again.');
      }
    }
  };

  const handleRegisterClick = (e) => {
    e.preventDefault();
    logout(); // Clear auth state before navigating
    navigate('/register');
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: '#F9F9F9',
        position: 'relative',
        overflow: 'hidden',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          width: '60vw',
          height: '40vh',
          background: 'radial-gradient(circle at 20% 0%, #007AFF22 0%, transparent 80%)',
          zIndex: 0,
        },
        '&::after': {
          content: '""',
          position: 'absolute',
          bottom: 0,
          right: 0,
          width: '60vw',
          height: '40vh',
          background: 'radial-gradient(circle at 80% 100%, #32CD3222 0%, transparent 80%)',
          zIndex: 0,
        },
      }}
    >
      {/* Decorative SVGs for extra Google-like feel */}
      <Box sx={{ position: 'absolute', top: 32, left: 24, zIndex: 1, pointerEvents: 'none' }}>
        <svg width="120" height="80" viewBox="0 0 120 80" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="40" cy="40" r="32" fill="#007AFF" fillOpacity="0.10" />
          <ellipse cx="90" cy="30" rx="18" ry="12" fill="#32CD32" fillOpacity="0.12" />
        </svg>
      </Box>
      <Box sx={{ position: 'absolute', bottom: 24, right: 24, zIndex: 1, pointerEvents: 'none' }}>
        <svg width="120" height="80" viewBox="0 0 120 80" fill="none" xmlns="http://www.w3.org/2000/svg">
          <ellipse cx="80" cy="60" rx="32" ry="18" fill="#32CD32" fillOpacity="0.10" />
          <circle cx="30" cy="30" r="18" fill="#007AFF" fillOpacity="0.10" />
        </svg>
      </Box>
      {/* Floating accent icons */}
      <CalendarTodayIcon sx={{ position: 'absolute', top: 80, right: 60, fontSize: 40, color: '#007AFF', opacity: 0.13, zIndex: 2 }} />
      <ChatIcon sx={{ position: 'absolute', bottom: 100, left: 60, fontSize: 44, color: '#32CD32', opacity: 0.13, zIndex: 2 }} />
      <DirectionsRunIcon sx={{ position: 'absolute', top: 180, left: 30, fontSize: 48, color: '#007AFF', opacity: 0.10, zIndex: 2 }} />
      <Container maxWidth="xs" sx={{ position: 'relative', zIndex: 3 }}>
        {/* Decorative SVG Illustration */}
        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
          <svg width="120" height="60" viewBox="0 0 120 60" fill="none" xmlns="http://www.w3.org/2000/svg">
            <ellipse cx="30" cy="30" rx="30" ry="18" fill="#007AFF" fillOpacity="0.12" />
            <ellipse cx="90" cy="30" rx="24" ry="14" fill="#32CD32" fillOpacity="0.10" />
            <path d="M10 40 Q 60 10 110 40" stroke="#007AFF" strokeWidth="2.5" fill="none" opacity="0.18" />
            <circle cx="60" cy="30" r="8" fill="#32CD32" fillOpacity="0.18" />
          </svg>
        </Box>
        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7 }}
        >
          <Box
            sx={{
              p: 4,
              background: '#FFFFFF',
              borderRadius: 4,
              border: '1px solid #E0E6ED',
              boxShadow: '0 8px 32px 0 rgba(0, 122, 255, 0.13)',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              position: 'relative',
            }}
          >
            <Box sx={{ mb: 2, display: 'flex', justifyContent: 'center', width: '100%' }}>
              <img
                src={process.env.PUBLIC_URL + '/logo192.png'}
                alt="Tempo Logo"
                style={{ width: 64, height: 64, borderRadius: '50%', boxShadow: '0 2px 8px rgba(0,122,255,0.10)' }}
              />
            </Box>
            <Typography
              variant="h3"
              sx={{
                mb: 1,
                textAlign: 'center',
                fontWeight: 700,
                color: '#007AFF',
                letterSpacing: '-0.02em',
                fontSize: '2rem',
              }}
            >
              Welcome Back
            </Typography>
            <Typography
              variant="subtitle1"
              sx={{
                mb: 3,
                textAlign: 'center',
                color: '#2E2E2E',
                fontWeight: 500,
                fontSize: '1.1rem',
              }}
            >
              Sign in to your account
            </Typography>
            <Divider sx={{ width: '100%', mb: 3, bgcolor: 'rgba(0, 122, 255, 0.10)' }} />

            {error && (
              <Alert 
                severity="error" 
                sx={{ 
                  mb: 2,
                  backgroundColor: 'rgba(0, 122, 255, 0.08)',
                  color: '#007AFF',
                  width: '100%',
                }}
              >
                {error}
              </Alert>
            )}

            {success && (
              <Alert 
                severity="success" 
                sx={{ 
                  mb: 2,
                  backgroundColor: 'rgba(50, 205, 50, 0.10)',
                  color: '#32CD32',
                  width: '100%',
                }}
              >
                {success}
              </Alert>
            )}

            <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
              <Box sx={{ mb: 3 }}>
                <TextField
                  required
                  fullWidth
                  label="Email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <EmailOutlinedIcon sx={{ color: '#007AFF' }} />
                      </InputAdornment>
                    ),
                  }}
                />
              </Box>

              <Box sx={{ mb: 4 }}>
                <TextField
                  required
                  fullWidth
                  label="Password"
                  name="password"
                  type="password"
                  value={formData.password}
                  onChange={handleChange}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <LockOutlinedIcon sx={{ color: '#007AFF' }} />
                      </InputAdornment>
                    ),
                  }}
                />
              </Box>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  py: 2,
                  mb: 3,
                  fontSize: '1.1rem',
                  fontWeight: 600,
                  background: 'linear-gradient(135deg, #007AFF 0%, #32CD32 100%)',
                  borderRadius: 2,
                  boxShadow: '0 4px 20px rgba(0, 122, 255, 0.08)',
                  color: '#fff',
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    background: 'linear-gradient(135deg, #32CD32 0%, #007AFF 100%)',
                    boxShadow: '0 6px 25px rgba(50, 205, 50, 0.12)',
                  },
                }}
              >
                Sign In
              </Button>

              <Box
                sx={{
                  textAlign: 'center',
                  position: 'relative',
                  mt: 1,
                  '&::before, &::after': {
                    content: '""',
                    position: 'absolute',
                    top: '50%',
                    width: '30%',
                    height: '1px',
                    background: 'linear-gradient(90deg, transparent, #E0E6ED)',
                  },
                  '&::before': {
                    left: 0,
                  },
                  '&::after': {
                    right: 0,
                    transform: 'rotate(180deg)',
                  },
                }}
              >
                <Link
                  component="button"
                  variant="button"
                  onClick={handleRegisterClick}
                  sx={{
                    color: '#007AFF',
                    textDecoration: 'none',
                    fontWeight: 600,
                    transition: 'all 0.2s',
                    '&:hover': {
                      color: '#32CD32',
                    },
                  }}
                >
                  Don't have an account? Sign up
                </Link>
              </Box>
            </Box>
          </Box>
        </motion.div>
      </Container>
    </Box>
  );
}

export default Login;
