import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material';
import CssBaseline from '@mui/material/CssBaseline';
import { AuthProvider } from './context/AuthContext';
import { useAuth } from './context/AuthContext';

// Component imports
import Navbar from './components/Layout/Navbar';
import Home from './components/Home/Home';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Dashboard from './components/Dashboard/Dashboard';
import TaskScheduler from './components/Tasks/TaskScheduler';
import Calendar from './components/Calendar/Calendar';


const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();
  return isAuthenticated ? children : <Navigate to="/login" />;
};

const PublicRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();
  return !isAuthenticated ? children : <Navigate to="/dashboard" />;
};

const YNRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();
  return !isAuthenticated ? children : <Navigate to="/calendar" />;
};

// Theme configuration
const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#DC143C',
      light: '#FF4D6A',
      dark: '#B30F32',
    },
    secondary: {
      main: '#1A1A1A',
      light: '#2C2C2C',
      dark: '#000000',
    },
    background: {
      default: '#0A0A0F',
      paper: '#111111',
    },
    text: {
      primary: '#F9FAFB',
      secondary: 'rgba(255, 255, 255, 0.7)',
    },
    error: {
      main: '#FF6B6B',
    },
    success: {
      main: '#4CAF50',
    },
    divider: 'rgba(220, 20, 60, 0.1)',
  },
  typography: {
    fontFamily: [
      'Plus Jakarta Sans',
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      'Arial',
      'sans-serif',
    ].join(','),
    h1: {
      fontWeight: 700,
      letterSpacing: '-0.025em',
      lineHeight: 1.1,
    },
    h2: {
      fontWeight: 700,
      letterSpacing: '-0.02em',
      lineHeight: 1.2,
    },
    h3: {
      fontWeight: 600,
      letterSpacing: '-0.015em',
      lineHeight: 1.3,
    },
    h4: {
      fontWeight: 600,
      letterSpacing: '-0.01em',
      lineHeight: 1.3,
    },
    h5: {
      fontWeight: 600,
      letterSpacing: '-0.005em',
      lineHeight: 1.4,
    },
    h6: {
      fontWeight: 600,
      letterSpacing: 0,
      lineHeight: 1.4,
    },
    button: {
      fontWeight: 600,
      textTransform: 'none',
      letterSpacing: '0.02em',
    },
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          scrollbarColor: "#DC143C #111111",
          "&::-webkit-scrollbar, & *::-webkit-scrollbar": {
            width: 8,
          },
          "&::-webkit-scrollbar-thumb, & *::-webkit-scrollbar-thumb": {
            borderRadius: 8,
            backgroundColor: "#DC143C",
            minHeight: 24,
          },
          "&::-webkit-scrollbar-track, & *::-webkit-scrollbar-track": {
            backgroundColor: "#111111",
          },
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'rgba(10, 10, 15, 0.8)',
          backdropFilter: 'blur(20px)',
          borderBottom: '1px solid rgba(220, 20, 60, 0.1)',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          padding: '12px 24px',
          fontSize: '0.95rem',
          fontWeight: 600,
          transition: 'all 0.2s ease-in-out',
          textTransform: 'none',
        },
        contained: {
          background: 'linear-gradient(135deg, #DC143C 0%, #B30F32 100%)',
          boxShadow: '0 4px 14px 0 rgba(220, 20, 60, 0.39)',
          '&:hover': {
            background: 'linear-gradient(135deg, #FF4D6A 0%, #DC143C 100%)',
            boxShadow: '0 6px 20px rgba(220, 20, 60, 0.5)',
            transform: 'translateY(-1px)',
          },
        },
        outlined: {
          borderWidth: '2px',
          borderColor: 'rgba(220, 20, 60, 0.5)',
          '&:hover': {
            borderWidth: '2px',
            borderColor: '#DC143C',
            background: 'rgba(220, 20, 60, 0.08)',
          },
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 20,
          background: 'linear-gradient(135deg, rgba(17, 17, 17, 0.9) 0%, rgba(17, 17, 17, 0.8) 100%)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(220, 20, 60, 0.1)',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
          transition: 'all 0.3s ease-in-out',
          '&:hover': {
            transform: 'translateY(-5px)',
            boxShadow: '0 8px 30px rgba(220, 20, 60, 0.2)',
            border: '1px solid rgba(220, 20, 60, 0.2)',
          },
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            backgroundColor: 'rgba(17, 17, 17, 0.7)',
            backdropFilter: 'blur(20px)',
            '& fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.23)',
              borderWidth: '1px',
            },
            '&:hover fieldset': {
              borderColor: 'rgba(220, 20, 60, 0.5)',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#DC143C',
            },
            '& input': {
              backgroundColor: 'transparent',
            },
            '& input:-webkit-autofill': {
              '-webkit-box-shadow': '0 0 0 30px #111111 inset !important',
              '-webkit-text-fill-color': '#ffffff !important',
              'caret-color': '#ffffff',
            },
          },
          '& .MuiInputLabel-root': {
            color: 'rgba(255, 255, 255, 0.7)',
          },
          '& .MuiInputBase-input': {
            color: '#FFFFFF',
          },
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          background: 'linear-gradient(135deg, rgba(17, 17, 17, 0.9) 0%, rgba(17, 17, 17, 0.8) 100%)',
          backdropFilter: 'blur(20px)',
          borderRadius: 20,
        },
      },
    },
    MuiDialog: {
      styleOverrides: {
        paper: {
          borderRadius: 20,
          background: 'linear-gradient(135deg, rgba(17, 17, 17, 0.95) 0%, rgba(17, 17, 17, 0.9) 100%)',
          backdropFilter: 'blur(40px)',
        },
      },
    },
    MuiLink: {
      styleOverrides: {
        root: {
          color: '#DC143C',
          textDecoration: 'none',
          transition: 'all 0.2s ease-in-out',
          '&:hover': {
            color: '#FF4D6A',
          },
        },
      },
    },
  },
  shape: {
    borderRadius: 12,
  },
});

function App() {
  return (
    <AuthProvider>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Router>
          <div className="App" style={{ 
            background: 'linear-gradient(135deg, #0A0A0F 0%, #111111 100%)',
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
          }}>
            <Navbar />
            <Routes>
              {/* Public routes */}
              <Route path="/" element={<Home />} />
              <Route 
                path="/login" 
                element={
                  <PublicRoute>
                    <Login />
                  </PublicRoute>
                } 
              />
              <Route 
                path="/register" 
                element={
                  <PublicRoute>
                    <Register />
                  </PublicRoute>
                } 
              />

              <Route 
                path="/tasks" 
                element={
                  <ProtectedRoute>
                  <TaskScheduler />
                  </ProtectedRoute>
                } 
              />  

              {/* Protected routes */}
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <Dashboard />
                  </ProtectedRoute>
                } 
              />

              {/* Fallback route */}
              <Route path="*" element={<Navigate to="/" />} />
            </Routes>
          </div>
        </Router>
      </ThemeProvider>
    </AuthProvider>
  );
}

export default App;
