  DialogContent,
  DialogActions,
  TextField,
  Button,
  MenuItem
} from '@mui/material';
import { 
  format, 
  startOfMonth, 
  endOfMonth, 
  eachDayOfInterval,
  isSameMonth,
  isSameDay,
  addMonths,
  subMonths,
  startOfWeek,
  endOfWeek,
  eachHourOfInterval,
  startOfDay,
  endOfDay,
  addDays,
  parseISO
} from 'date-fns';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import CalendarViewMonthIcon from '@mui/icons-material/CalendarViewMonth';
import CalendarViewDayIcon from '@mui/icons-material/CalendarViewDay';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

function Calendar() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [view, setView] = useState('month');
  const [editingTask, setEditingTask] = useState(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tasks`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTasks(response.data);
    } catch (err) {
      console.error('Failed to fetch tasks:', err);
    } finally {
      setLoading(false);
    }
  };

  const getDaysInMonth = () => {
    const start = startOfMonth(currentDate);
    const end = endOfMonth(currentDate);
    return eachDayOfInterval({ start, end });
  };

  const getTasksForDate = (date) => {
    return tasks.filter(task => 
      task.due_date && isSameDay(new Date(task.due_date), date)
    );
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setDrawerOpen(true);
  };

  const priorityColors = {
    high: '#DC143C',
    medium: '#FFA500',
    low: '#4CAF50'
  };

  const handleViewChange = (event, newView) => {
    if (newView !== null) {
      setView(newView);
    }
  };

  const getTasksForHour = (date, hour) => {
    return tasks.filter(task => {
      if (!task.due_date) return false;
      const taskDate = parseISO(task.due_date);
      return isSameDay(taskDate, date) && taskDate.getHours() === hour;
    });
  };

  const renderDayView = () => {
    const hours = eachHourOfInterval({
      start: startOfDay(selectedDate || new Date()),
      end: endOfDay(selectedDate || new Date())
    });

    return (
      <Box sx={{ mt: 3 }}>
        {hours.map((hour) => {
          const tasksForHour = getTasksForHour(selectedDate || new Date(), hour.getHours());
          return (
            <Paper
              key={hour.toString()}
              sx={{
                mb: 2,
                p: 2,
                background: 'rgba(255, 255, 255, 0.03)',
                borderRadius: 2,
                border: '1px solid rgba(255, 255, 255, 0.1)',
              }}
            >
              <Typography sx={{ color: '#fff', mb: 1 }}>
                {format(hour, 'h:00 a')}
              </Typography>
              {tasksForHour.length > 0 ? (
                tasksForHour.map(task => (
                  <Box
                    key={task.id}
                    sx={{
                      p: 1.5,
                      mt: 1,
                      background: 'rgba(255, 255, 255, 0.05)',
                      borderRadius: 1,
                      border: '1px solid rgba(255, 255, 255, 0.1)',
                    }}
                  >
                    <Typography sx={{ color: '#fff' }}>{task.title}</Typography>
                    <Chip
                      label={task.priority}
                      size="small"
                      sx={{
                        mt: 1,
                        backgroundColor: `${priorityColors[task.priority]}15`,
                        color: priorityColors[task.priority],
                      }}
                    />
                  </Box>
                ))
              ) : (
                <Typography sx={{ color: 'rgba(255, 255, 255, 0.3)', fontSize: '0.9rem' }}>
                  No tasks scheduled
                </Typography>
              )}
            </Paper>
          );
        })}
      </Box>
    );
  };

  const renderMonthView = () => {
    const start = startOfWeek(startOfMonth(currentDate));
    const end = endOfWeek(endOfMonth(currentDate));
    const days = eachDayOfInterval({ start, end });

    return (
      <>
        <Grid container spacing={2} sx={{ mb: 2 }}>
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
            <Grid item xs key={day}>
              <Typography
                align="center"
                sx={{ 
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontSize: '0.9rem',
                  fontWeight: 500,
                  letterSpacing: '1px',
                }}
              >
                {day}
              </Typography>
            </Grid>
          ))}
        </Grid>

        <DragDropContext onDragEnd={handleDragEnd}>
          <Grid container spacing={0.5}>
            {days.map((date, index) => {
              const tasksForDate = getTasksForDate(date);
              return (
                <Grid item xs={12} sm={6} md={1.714} key={date.toString()}>
                  <Droppable droppableId={date.toISOString()}>
                    {(provided) => (
                      <Paper
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                        onClick={() => {
                          setSelectedDate(date);
                          setView('day');
                        }}
                        sx={{
                          p: 2,
                          minHeight: { xs: 80, sm: 100, md: 120 },
                          textAlign: 'left',
                          cursor: 'pointer',
                          background: isSameDay(date, new Date()) 
                            ? 'rgba(220, 20, 60, 0.15)'
                            : 'rgba(255, 255, 255, 0.03)',
                          '&:hover': {
                            background: 'rgba(255, 255, 255, 0.1)',
                            transform: 'translateY(-2px)',
                          },
                          border: isSameDay(date, new Date())
                            ? '1px solid rgba(220, 20, 60, 0.3)'
                            : '1px solid rgba(255, 255, 255, 0.1)',
                          transition: 'all 0.2s ease-in-out',
                          opacity: isSameMonth(date, currentDate) ? 1 : 0.5,
                        }}
                      >
                        <Typography sx={{ color: '#fff', fontSize: '1.1rem', mb: 1 }}>
                          {format(date, 'd')}
                        </Typography>
                        {tasksForDate.map((task, taskIndex) => (
                          <Draggable
                            key={task.id}
                            draggableId={task.id}
                            index={taskIndex}
                          >
                            {(provided) => (
                              <Box
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                sx={{
                                  p: 1,
                                  mb: 1,
                                  borderRadius: 1,
                                  backgroundColor: `${priorityColors[task.priority]}15`,
                                  border: `1px solid ${priorityColors[task.priority]}30`,
                                }}
                              >
                                <Typography sx={{ color: '#fff', fontSize: '0.8rem' }}>
                                  {task.title}
                                </Typography>
                              </Box>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </Paper>
                    )}
                  </Droppable>
                </Grid>
              );
            })}
          </Grid>
        </DragDropContext>
      </>
    );
  };

  const handleTaskEdit = async (taskId, updatedData) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`${API_URL}/tasks/${taskId}`, updatedData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      await fetchTasks();
      setIsEditDialogOpen(false);
    } catch (err) {
      console.error('Failed to update task:', err);
    }
  };

  const handleDragEnd = async (result) => {
    if (!result.destination) return;

    const taskId = result.draggableId;
    const newDate = addDays(startOfMonth(currentDate), result.destination.index);

    try {
      const task = tasks.find(t => t.id === taskId);
      await handleTaskEdit(taskId, { ...task, due_date: newDate });
    } catch (err) {
      console.error('Failed to update task date:', err);
    }
  };

  // Add Task Edit Dialog
  const TaskEditDialog = () => (
    <Dialog 
      open={isEditDialogOpen} 
      onClose={() => setIsEditDialogOpen(false)}
      PaperProps={{
        sx: {
          background: 'rgba(17, 17, 17, 0.95)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
        }
      }}
    >
      <DialogTitle sx={{ color: '#fff' }}>Edit Task</DialogTitle>
      <DialogContent>
        <TextField
          fullWidth
          margin="dense"
          label="Title"
          value={editingTask?.title || ''}
          onChange={(e) => setEditingTask({ ...editingTask, title: e.target.value })}
          sx={{ mb: 2 }}
        />
        <TextField
          select
          fullWidth
          margin="dense"
          label="Priority"
          value={editingTask?.priority || 'medium'}
          onChange={(e) => setEditingTask({ ...editingTask, priority: e.target.value })}
        >
          {['low', 'medium', 'high'].map((priority) => (
            <MenuItem key={priority} value={priority}>
              {priority.charAt(0).toUpperCase() + priority.slice(1)}
            </MenuItem>
          ))}
        </TextField>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setIsEditDialogOpen(false)}>Cancel</Button>
        <Button 
          onClick={() => handleTaskEdit(editingTask.id, editingTask)}
          variant="contained"
        >
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );

  return (
    <Box
      sx={{
        minHeight: 'calc(100vh - 64px)',
        background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
        py: 6,
        px: 4,
        display: 'flex',
        justifyContent: 'center',
      }}
    >
      <Paper
        elevation={0}
        sx={{
          p: 4,
          background: 'rgba(17, 17, 17, 0.6)',
          backdropFilter: 'blur(20px)',
          borderRadius: 4,
          border: '1px solid rgba(255, 255, 255, 0.1)',
          maxWidth: 1200,
          width: '100%',
          boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
        }}
      >
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center', 
          mb: 5, 
          justifyContent: 'space-between',
          px: 2 
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <IconButton 
              onClick={() => setCurrentDate(subMonths(currentDate, 1))}
              sx={{
                background: 'rgba(255, 255, 255, 0.05)',
                '&:hover': {
                  background: 'rgba(220, 20, 60, 0.1)',
                }
              }}
            >
              <ChevronLeftIcon sx={{ color: '#fff' }} />
            </IconButton>
            <Typography variant="h4" sx={{ 
              color: '#fff', 
              fontWeight: 600,
              letterSpacing: '-0.5px',
              background: 'linear-gradient(45deg, #fff, rgba(255, 255, 255, 0.7))',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}>
              {view === 'month' 
                ? format(currentDate, 'MMMM yyyy')
                : format(selectedDate || new Date(), 'MMMM d, yyyy')}
            </Typography>
            <IconButton 
              onClick={() => setCurrentDate(addMonths(currentDate, 1))}
              sx={{
                background: 'rgba(255, 255, 255, 0.05)',
                '&:hover': {
                  background: 'rgba(220, 20, 60, 0.1)',
                }
              }}
            >
              <ChevronRightIcon sx={{ color: '#fff' }} />
            </IconButton>
          </Box>

          <ToggleButtonGroup
            value={view}
            exclusive
            onChange={handleViewChange}
            sx={{
              '& .MuiToggleButton-root': {
                color: '#fff',
                borderColor: 'rgba(255, 255, 255, 0.1)',
                '&.Mui-selected': {
                  backgroundColor: 'rgba(220, 20, 60, 0.2)',
                  color: '#DC143C',
                }
              }
            }}
          >
            <ToggleButton value="month">
              <CalendarViewMonthIcon />
            </ToggleButton>
            <ToggleButton value="day">
              <CalendarViewDayIcon />
            </ToggleButton>
          </ToggleButtonGroup>
        </Box>

        {view === 'month' ? renderMonthView() : renderDayView()}
      </Paper>

      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          style: {
            width: 380,
            background: 'rgba(17, 17, 17, 0.95)',
            backdropFilter: 'blur(20px)',
            borderLeft: '1px solid rgba(255, 255, 255, 0.1)',
          }
        }}
      >
        {selectedDate && (
          <Box sx={{ p: 4 }}>
            <Typography variant="h5" sx={{ 
              color: '#fff', 
              mb: 3,
              fontWeight: 600,
              background: 'linear-gradient(45deg, #fff, rgba(255, 255, 255, 0.7))',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}>
              Tasks for {format(selectedDate, 'MMMM d, yyyy')}
            </Typography>
            <List>
              {getTasksForDate(selectedDate).map((task) => (
                <ListItem
                  key={task.id}
                  sx={{
                    mb: 2,
                    background: 'rgba(255, 255, 255, 0.03)',
                    borderRadius: 2,
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    p: 2,
                    transition: 'all 0.2s ease-in-out',
                    '&:hover': {
                      background: 'rgba(255, 255, 255, 0.05)',
                      transform: 'translateX(-4px)',
                    },
                  }}
                >
                  <ListItemText
                    primary={
                      <Typography sx={{ 
                        color: '#fff',
                        fontSize: '1.1rem',
                        fontWeight: 500,
                        mb: 1
                      }}>
                        {task.title}
                      </Typography>
                    }
                    secondary={
                      <Box sx={{ mt: 1 }}>
                        <Chip
                          label={task.priority}
                          size="small"
                          sx={{
                            backgroundColor: `${priorityColors[task.priority]}15`,
                            color: priorityColors[task.priority],
                            mr: 1,
                            fontWeight: 500,
                          }}
                        />
                        {task.estimated_time && (
                          <Chip
                            label={`${task.estimated_time}h`}
                            size="small"
                            sx={{
                              backgroundColor: 'rgba(255, 255, 255, 0.1)',
                              color: '#fff',
                              fontWeight: 500,
                            }}
                          />
                        )}
                      </Box>
                    }
                  />
                </ListItem>
              ))}
              {getTasksForDate(selectedDate).length === 0 && (
                <Typography sx={{ 
                  color: 'rgba(255, 255, 255, 0.5)', 
                  textAlign: 'center',
                  py: 4,
                  fontSize: '1.1rem'
                }}>
                  No tasks for this date
                </Typography>
              )}
            </List>
          </Box>
        )}
      </Drawer>

      {loading && (
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
          }}
        >
          <CircularProgress />
        </Box>
      )}
    </Box>
  );
}

export default Calendar;
