import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper,
  Grid,
  IconButton,
  Badge,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Chip,
  CircularProgress,
  ToggleButton,
  ToggleButtonGroup,
  Divider
} from '@mui/material';
import { 
  format, 
  startOfMonth, 
  endOfMonth, 
  eachDayOfInterval,
  isSameMonth,
  isSameDay,
  addMonths,
  subMonths,
  startOfWeek,
  endOfWeek,
  eachHourOfInterval,
  startOfDay,
  endOfDay,
  addDays,
  parseISO,
  isWithinInterval
} from 'date-fns';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import CalendarViewMonthIcon from '@mui/icons-material/CalendarViewMonth';
import CalendarViewDayIcon from '@mui/icons-material/CalendarViewDay';
import axios from 'axios';
import { motion } from 'framer-motion';

const API_URL = 'http://127.0.0.1:5000';

function Calendar() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [view, setView] = useState('month');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tasks`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTasks(response.data);
    } catch (err) {
      console.error('Failed to fetch tasks:', err);
    } finally {
      setLoading(false);
    }
  };

  const getDaysInMonth = () => {
    const start = startOfMonth(currentDate);
    const end = endOfMonth(currentDate);
    return eachDayOfInterval({ start, end });
  };

  const getTasksForDate = (date) => {
    return tasks.filter(task => 
      task.due_date && isSameDay(new Date(task.due_date), date)
    );
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setDrawerOpen(true);
  };

  // Update the priorityColors to match the image's color scheme
  const priorityColors = {
    high: '#E91E63', // Pink
    medium: '#2196F3', // Blue
    low: '#9C27B0', // Purple
    default: '#00BCD4' // Teal
  };

  // Add new type colors
    const typeColors = {
      meeting: '#2196F3',    // Blue
      personal: '#FF9800',   // Orange
      task: '#E91E63',       // Pink
      break: '#90CAF9',      // Light Blue
    };

    const renderDayView = () => {
      const hours = eachHourOfInterval({
        start: startOfDay(selectedDate || new Date()),
        end: endOfDay(selectedDate || new Date())
      });

      const weekStart = startOfWeek(selectedDate || new Date());
      const weekDays = Array.from({ length: 5 }, (_, i) => addDays(weekStart, i + 1));

      return (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
        >
          <Box sx={{ 
            width: '100%',
            height: '700px',
            backgroundColor: 'rgba(30, 30, 30, 0.6)',
            borderRadius: 1,
            overflow: 'hidden',
            border: '1px solid rgba(255, 255, 255, 0.1)',
          }}>
            {/* Header */}
            <Box sx={{ 
              display: 'flex',
              borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
              p: 2,
            }}>
              <Box sx={{ width: '80px' }}></Box>
              {weekDays.map(day => (
                <Box 
                  key={day.toString()}
                  sx={{ 
                    flex: 1,
                    textAlign: 'center',
                    color: '#fff',
                  }}
                >
                  <Typography sx={{ opacity: 0.7, fontSize: '0.9rem' }}>
                    {format(day, 'EEE d')}
                  </Typography>
                </Box>
              ))}
            </Box>

            {/* Calendar Grid */}
            <Box sx={{ 
              height: 'calc(100% - 60px)',
              overflow: 'auto',
              position: 'relative',
            }}>
              {hours.map((hour) => (
                <Box
                  key={hour.toString()}
                  sx={{
                    display: 'flex',
                    minHeight: '60px',
                    borderBottom: '1px solid rgba(255, 255, 255, 0.05)',
                  }}
                >
                  <Typography 
                    sx={{ 
                      width: '50px',
                      color: 'rgba(255, 255, 255, 0.5)',
                      fontSize: '0.75rem',
                      py: 1,
                      pr: 2,
                      textAlign: 'right',
                      position: 'sticky',
                      left: 0,
                    }}
                  >
                    {format(hour, 'h a')}
                  </Typography>

                  {weekDays.map(day => (
                    <Box
                      key={day.toString()}
                      sx={{
                        flex: 1,
                        borderLeft: '1px solid rgba(255, 255, 255, 0.05)',
                        position: 'relative',
                        minHeight: '60px',
                      }}
                    >
                      {getTasksForHour(day, hour.getHours()).map(task => (
                        <Paper
                          key={task.id}
                          sx={{
                            position: 'absolute',
                            left: '4px',
                            right: '4px',
                            top: '4px',
                            minHeight: '50px',
                            backgroundColor: typeColors[task.type] || typeColors.task,
                            p: 1,
                            borderRadius: 1,
                            display: 'flex',
                            flexDirection: 'column',
                            gap: 0.5,
                            cursor: 'pointer',
                            '&:hover': {
                              filter: 'brightness(1.1)',
                            },
                          }}
                        >
                          <Typography sx={{ 
                            fontSize: '0.85rem',
                            fontWeight: 500,
                            color: '#fff'
                          }}>
                            {task.title}
                          </Typography>
                          {task.duration && (
                            <Typography sx={{ 
                              fontSize: '0.75rem',
                              color: 'rgba(255, 255, 255, 0.8)'
                            }}>
                              {task.duration}
                            </Typography>
                          )}
                        </Paper>
                      ))}
                    </Box>
                  ))}
                </Box>
              ))}
            </Box>
          </Box>
        </motion.div>
      );
    };

  // Update the month view task rendering
  const renderMonthView = () => {
    const start = startOfWeek(startOfMonth(currentDate));
    const end = endOfWeek(endOfMonth(currentDate));
    const days = eachDayOfInterval({ start, end });

    return (
      <>
        <Grid container spacing={2} sx={{ mb: 2 }}>
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
            <Grid item xs key={day}>
              <Typography
                align="center"
                sx={{ 
                  color: 'rgba(255, 255, 255, 0.7)',
                  fontSize: '0.9rem',
                  fontWeight: 500,
                  letterSpacing: '1px',
                }}
              >
                {day}
              </Typography>
            </Grid>
          ))}
        </Grid>

        <Grid container spacing={0.5}>
          {days.map((date) => {
            const tasksForDate = getTasksForDate(date);
            return (
              <Grid item xs={1.714} key={date.toString()}>
                <Paper
                  onClick={() => {
                    setSelectedDate(date);
                    setView('day');
                  }}
                  sx={{
                    p: 1.5,
                    height: 120,
                    textAlign: 'left',
                    cursor: 'pointer',
                    background: 'rgba(30, 30, 30, 0.6)',
                    '&:hover': {
                      background: 'rgba(40, 40, 40, 0.8)',
                    },
                    borderRadius: 1,
                    transition: 'all 0.2s ease-in-out',
                    opacity: isSameMonth(date, currentDate) ? 1 : 0.5,
                  }}
                >
                  <Typography sx={{
                    color: '#fff',
                    fontSize: '0.9rem',
                    mb: 1,
                  }}>
                    {format(date, 'd')}
                  </Typography>
                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                    {tasksForDate.map((task, index) => index < 3 && (
                      <Box
                        key={task.id}
                        sx={{
                          backgroundColor: `${priorityColors[task.priority] || priorityColors.default}`,
                          color: '#fff',
                          p: 0.8,
                          borderRadius: 0.5,
                          fontSize: '0.8rem',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                        }}
                      >
                        {task.title}
                      </Box>
                    ))}
                  </Box>
                </Paper>
              </Grid>
            );
          })}
        </Grid>
      </>
    );
  };

  const getTasksForHour = (date, hour) => {
    return tasks.filter(task => {
      if (!task.due_date) return false;
      const taskDate = parseISO(task.due_date);
      return isSameDay(taskDate, date) && taskDate.getHours() === hour;
    });
  };

  const handleViewChange = (event, newView) => {
    if (newView !== null) {
      setView(newView);
    }
  };

  return (
    <Box
      sx={{
        minHeight: 'calc(100vh - 64px)',
        background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
        py: 6,
        px: 4,
        display: 'flex',
        justifyContent: 'center',
      }}
    >
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, ease: "easeOut" }}
      >
        <Paper
          elevation={0}
          sx={{
            p: 4,
            background: 'rgba(17, 17, 17, 0.6)',
            backdropFilter: 'blur(20px)',
            borderRadius: 4,
            border: '1px solid rgba(255, 255, 255, 0.1)',
            maxWidth: 1600, // Increased from 1200
            width: '100%',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
          }}
        >
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            mb: 5, 
            justifyContent: 'space-between',
            px: 2 
          }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <IconButton 
                onClick={() => setCurrentDate(subMonths(currentDate, 1))}
                sx={{
                  background: 'rgba(255, 255, 255, 0.05)',
                  '&:hover': {
                    background: 'rgba(220, 20, 60, 0.1)',
                  }
                }}
              >
                <ChevronLeftIcon sx={{ color: '#fff' }} />
              </IconButton>
              <Typography variant="h4" sx={{ 
                color: '#fff', 
                fontWeight: 600,
                letterSpacing: '-0.5px',
                background: 'linear-gradient(45deg, #fff, rgba(255, 255, 255, 0.7))',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
              }}>
                {view === 'month' 
                  ? format(currentDate, 'MMMM yyyy')
                  : format(selectedDate || new Date(), 'MMMM d, yyyy')}
              </Typography>
              <IconButton 
                onClick={() => setCurrentDate(addMonths(currentDate, 1))}
                sx={{
                  background: 'rgba(255, 255, 255, 0.05)',
                  '&:hover': {
                    background: 'rgba(220, 20, 60, 0.1)',
                  }
                }}
              >
                <ChevronRightIcon sx={{ color: '#fff' }} />
              </IconButton>
            </Box>

            <ToggleButtonGroup
              value={view}
              exclusive
              onChange={handleViewChange}
              sx={{
                '& .MuiToggleButton-root': {
                  color: '#fff',
                  borderColor: 'rgba(255, 255, 255, 0.1)',
                  '&.Mui-selected': {
                    backgroundColor: 'rgba(220, 20, 60, 0.2)',
                    color: '#DC143C',
                  }
                }
              }}
            >
              <ToggleButton value="month">
                <CalendarViewMonthIcon />
              </ToggleButton>
              <ToggleButton value="day">
                <CalendarViewDayIcon />
              </ToggleButton>
            </ToggleButtonGroup>
          </Box>

          {view === 'month' ? renderMonthView() : renderDayView()}
        </Paper>
      </motion.div>

      <Drawer>
        {/* ... Drawer content ... */}
      </Drawer>

      {loading && (
        <Box>
          {/* ... Loading content ... */}
        </Box>
      )}
    </Box>
  );
}

export default Calendar;
