import React, { useState, useEffect } from 'react';
import { 
    Box, 
    Container, 
    Grid, 
    Card, 
    CardContent,
    Typography,
    IconButton,
    Tooltip,
    Button,
    Divider
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import AssignmentIcon from '@mui/icons-material/Assignment';
import ChatIcon from '@mui/icons-material/Chat';
import AutoGraphIcon from '@mui/icons-material/AutoGraph';
import AssistantIcon from '@mui/icons-material/Assistant';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { format, isSameDay } from 'date-fns';
import { CircularProgress } from '@mui/material';  // Add this import

function Dashboard() {
    const navigate = useNavigate();
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://127.0.0.1:5000/tasks', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setTasks(response.data);
        } catch (error) {
            console.error('Failed to fetch tasks:', error);
        } finally {
            setLoading(false);
        }
    };
    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 6,
                position: 'relative',
                '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    right: 0,
                    bottom: 0,
                    left: 0,
                    background: 'radial-gradient(circle at 50% 0%, rgba(220, 20, 60, 0.1) 0%, transparent 50%)',
                    pointerEvents: 'none'
                }
            }}
        >
            <Container maxWidth="xl">
                <motion.div
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <Card
                        sx={{
                            mb: 4,
                            background: 'rgba(17, 17, 17, 0.8)',
                            backdropFilter: 'blur(20px)',
                            borderRadius: 3,
                            border: '1px solid rgba(255, 255, 255, 0.1)',
                            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                        }}
                    >
                        <CardContent sx={{ p: 4 }}>
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                                <Typography variant="h4" sx={{ 
                                    display: 'flex', 
                                    alignItems: 'center', 
                                    gap: 2,
                                    color: 'rgba(255, 255, 255, 0.9)',
                                }}>
                                    <ChatIcon sx={{ color: '#DC143C', fontSize: 40 }} />
                                    AI Study Assistant
                                </Typography>
                                <Button
                                    variant="contained"
                                    startIcon={<AssistantIcon />}
                                    sx={{
                                        bgcolor: 'rgba(220, 20, 60, 0.8)',
                                        '&:hover': { bgcolor: 'rgba(220, 20, 60, 1)' },
                                        fontSize: '1rem',
                                        px: 3,
                                        py: 1.5
                                    }}
                                >
                                    Open Assistant
                                </Button>
                            </Box>
                            <Divider sx={{ my: 3, bgcolor: 'rgba(255, 255, 255, 0.1)' }} />
                            <Grid container spacing={4}>
                                <Grid item xs={12} md={8}>
                                    <Typography variant="h6" color="rgba(255, 255, 255, 0.7)" gutterBottom>
                                        Current Study Plan
                                    </Typography>
                                    <Box sx={{ mt: 2 }}>
                                        {/* Study plan content will go here */}
                                    </Box>
                                </Grid>
                                <Grid item xs={12} md={4}>
                                    <Typography variant="h6" color="rgba(255, 255, 255, 0.7)" gutterBottom>
                                        Study Metrics
                                    </Typography>
                                    {/* Real-time metrics will go here */}
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                </motion.div>

                <Grid container spacing={4}>
                    <Grid item xs={12} md={4}>
                        <motion.div
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ duration: 0.5, delay: 0.2 }}
                        >
                            <Card
                                sx={{
                                    height: '100%',
                                    background: 'rgba(17, 17, 17, 0.6)',
                                    backdropFilter: 'blur(20px)',
                                    borderRadius: 3,
                                    border: '1px solid rgba(255, 255, 255, 0.1)',
                                    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                                }}
                            >
                                <CardContent sx={{ height: '100%', p: 4 }}>
                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                                        <Typography variant="h5" sx={{ 
                                            display: 'flex', 
                                            alignItems: 'center', 
                                            gap: 1.5,
                                            color: 'rgba(255, 255, 255, 0.9)',
                                        }}>
                                            <CalendarTodayIcon sx={{ color: '#DC143C', fontSize: 30 }} />
                                            Schedule Manager
                                        </Typography>
                                        <Tooltip title="Manage Schedule">
                                            <IconButton 
                                                sx={{ color: '#DC143C' }}
                                                onClick={() => navigate('/calendar')}
                                            >
                                                <AddIcon />
                                            </IconButton>
                                        </Tooltip>

                                    </Box>
                                    {/* Schedule management interface will go here */}
                                </CardContent>
                            </Card>
                        </motion.div>
                    </Grid>

                    <Grid item xs={12} md={4}>
                        <motion.div
                            initial={{ opacity: 0, x: 20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ duration: 0.5, delay: 0.4 }}
                        >
                            <Card
                                sx={{
                                    height: '100%',
                                    background: 'rgba(17, 17, 17, 0.6)',
                                    backdropFilter: 'blur(20px)',
                                    borderRadius: 3,
                                    border: '1px solid rgba(255, 255, 255, 0.1)',
                                    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                                }}
                            >
                                // Fix the Task Manager card content
                                <CardContent sx={{ height: '100%', p: 4 }}>
                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                                        <Typography variant="h5" sx={{ 
                                            display: 'flex', 
                                            alignItems: 'center', 
                                            gap: 1.5,
                                            color: 'rgba(255, 255, 255, 0.9)',
                                        }}>
                                            <AssignmentIcon sx={{ color: '#DC143C', fontSize: 30 }} />
                                            Task Manager
                                        </Typography>
                                        <Tooltip title="Add Task">
                                            <IconButton 
                                                sx={{ color: '#DC143C' }}
                                                onClick={() => navigate('/tasks')}
                                            >
                                                <AddIcon />
                                            </IconButton>
                                        </Tooltip>
                                    </Box>

                                    {loading ? (
                                        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                                            <CircularProgress size={24} sx={{ color: '#DC143C' }} />
                                        </Box>
                                    ) : (
                                        <Box sx={{ mt: 2 }}>
                                            {tasks.filter(task => 
                                                task.due_date && isSameDay(new Date(task.due_date), new Date())
                                            ).length > 0 ? (
                                                tasks.filter(task => 
                                                    task.due_date && isSameDay(new Date(task.due_date), new Date())
                                                ).map(task => (
                                                    <Box
                                                        key={task.id}
                                                        sx={{
                                                            p: 2,
                                                            mb: 1,
                                                            borderRadius: 1,
                                                            backgroundColor: 'rgba(255, 255, 255, 0.05)',
                                                            '&:hover': { backgroundColor: 'rgba(255, 255, 255, 0.08)' }
                                                        }}
                                                    >
                                                        <Typography sx={{ 
                                                            color: '#fff', 
                                                            fontSize: '0.9rem',
                                                            fontWeight: 500 
                                                        }}>
                                                            {task.title}
                                                        </Typography>
                                                        <Typography sx={{ 
                                                            color: 'rgba(255, 255, 255, 0.5)', 
                                                            fontSize: '0.8rem',
                                                            mt: 0.5 
                                                        }}>
                                                            {format(new Date(task.due_date), 'h:mm a')}
                                                        </Typography>
                                                    </Box>
                                                ))
                                            ) : (
                                                <Typography sx={{ 
                                                    color: 'rgba(255, 255, 255, 0.5)',
                                                    textAlign: 'center',
                                                    mt: 2
                                                }}>
                                                    No tasks scheduled for today
                                                </Typography>
                                            )}
                                        </Box>
                                    )}
                                </CardContent>
                            </Card>
                        </motion.div>
                    </Grid>

                    <Grid item xs={12} md={4}>
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: 0.6 }}
                        >
                            <Card
                                sx={{
                                    height: '100%',
                                    background: 'rgba(17, 17, 17, 0.6)',
                                    backdropFilter: 'blur(20px)',
                                    borderRadius: 3,
                                    border: '1px solid rgba(255, 255, 255, 0.1)',
                                    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                                }}
                            >
                                <CardContent sx={{ height: '100%', p: 4 }}>
                                    <Typography variant="h5" sx={{ 
                                        display: 'flex', 
                                        alignItems: 'center', 
                                        gap: 1.5,
                                        color: 'rgba(255, 255, 255, 0.9)',
                                        mb: 3
                                    }}>
                                        <AutoGraphIcon sx={{ color: '#DC143C', fontSize: 30 }} />
                                        Study Analytics
                                    </Typography>
                                    {/* Analytics dashboard will go here */}
                                </CardContent>
                            </Card>
                        </motion.div>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default Dashboard;
