import React from 'react';
import { 
    Box, 
    Typography, 
    Container, 
    Grid, 
    Card, 
    CardContent,
    IconButton,
    Divider,
    Paper
} from '@mui/material';
import AssignmentIcon from '@mui/icons-material/Assignment';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import TimelineIcon from '@mui/icons-material/Timeline';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import ScheduleIcon from '@mui/icons-material/Schedule';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

function Dashboard() {
    // Example data - replace with real data later
    const stats = [
        {
            title: "Active Tasks",
            value: "12",
            icon: <AssignmentIcon />,
            color: "#DC143C"
        },
        {
            title: "Upcoming Exams",
            value: "3",
            icon: <CalendarTodayIcon />,
            color: "#4CAF50"
        },
        {
            title: "Study Hours",
            value: "28.5",
            icon: <TimelineIcon />,
            color: "#2196F3"
        },
        {
            title: "Completion Rate",
            value: "87%",
            icon: <TrendingUpIcon />,
            color: "#FFC107"
        }
    ];

    const upcomingTasks = [
        { title: "Math Assignment", due: "Today", status: "Urgent" },
        { title: "Physics Lab Report", due: "Tomorrow", status: "In Progress" },
        { title: "History Essay", due: "In 2 days", status: "Not Started" }
    ];

    const upcomingExams = [
        { subject: "Computer Science", date: "Dec 15", time: "10:00 AM" },
        { subject: "Statistics", date: "Dec 18", time: "2:00 PM" },
        { subject: "Economics", date: "Dec 20", time: "11:30 AM" }
    ];

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4,
                position: 'relative',
                '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: 0,
                    right: 0,
                    bottom: 0,
                    left: 0,
                    background: 'radial-gradient(circle at 50% 0%, rgba(220, 20, 60, 0.1) 0%, transparent 50%)',
                    pointerEvents: 'none'
                }
            }}
        >
            <Container maxWidth="xl">
                <Typography
                    variant="h3"
                    sx={{
                        mb: 4,
                        fontWeight: 700,
                        background: 'linear-gradient(45deg, #FFFFFF, #DC143C)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent',
                        letterSpacing: '-0.02em',
                        position: 'relative',
                        display: 'inline-block',
                        '&::after': {
                            content: '""',
                            position: 'absolute',
                            bottom: -8,
                            left: 0,
                            width: '60%',
                            height: 4,
                            background: 'linear-gradient(90deg, #DC143C, transparent)',
                            borderRadius: 2
                        }
                    }}
                >
                    Welcome Back
                </Typography>

                {/* Stats Grid */}
                <Grid container spacing={3} sx={{ mb: 4 }}>
                    {stats.map((stat, index) => (
                        <Grid item xs={12} sm={6} md={3} key={index}>
                            <Card
                                sx={{
                                    background: 'rgba(17, 17, 17, 0.6)',
                                    backdropFilter: 'blur(20px)',
                                    borderRadius: 4,
                                    border: '1px solid rgba(255, 255, 255, 0.1)',
                                    transition: 'transform 0.2s ease-in-out',
                                    '&:hover': {
                                        transform: 'translateY(-5px)',
                                    }
                                }}
                            >
                                <CardContent>
                                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                                        <IconButton
                                            sx={{
                                                backgroundColor: `${stat.color}20`,
                                                color: stat.color,
                                                '&:hover': { backgroundColor: `${stat.color}30` }
                                            }}
                                        >
                                            {stat.icon}
                                        </IconButton>
                                    </Box>
                                    <Typography variant="h4" sx={{ mb: 1, fontWeight: 700 }}>
                                        {stat.value}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        {stat.title}
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>

                {/* Tasks and Exams Grid */}
                <Grid container spacing={3}>
                    {/* Upcoming Tasks */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                            }}
                        >
                            <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 1 }}>
                                <AssignmentIcon sx={{ color: '#DC143C' }} />
                                Upcoming Tasks
                            </Typography>
                            <Divider sx={{ mb: 2, borderColor: 'rgba(255, 255, 255, 0.1)' }} />
                            {upcomingTasks.map((task, index) => (
                                <Box
                                    key={index}
                                    sx={{
                                        mb: 2,
                                        p: 2,
                                        borderRadius: 2,
                                        background: 'rgba(255, 255, 255, 0.05)',
                                        display: 'flex',
                                        justifyContent: 'space-between',
                                        alignItems: 'center'
                                    }}
                                >
                                    <Box>
                                        <Typography variant="subtitle1">{task.title}</Typography>
                                        <Typography variant="body2" color="text.secondary">Due: {task.due}</Typography>
                                    </Box>
                                    <CheckCircleIcon sx={{ color: task.status === 'Urgent' ? '#DC143C' : 'rgba(255, 255, 255, 0.3)' }} />
                                </Box>
                            ))}
                        </Paper>
                    </Grid>

                    {/* Upcoming Exams */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            sx={{
                                p: 3,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                            }}
                        >
                            <Typography variant="h6" sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 1 }}>
                                <ScheduleIcon sx={{ color: '#DC143C' }} />
                                Upcoming Exams
                            </Typography>
                            <Divider sx={{ mb: 2, borderColor: 'rgba(255, 255, 255, 0.1)' }} />
                            {upcomingExams.map((exam, index) => (
                                <Box
                                    key={index}
                                    sx={{
                                        mb: 2,
                                        p: 2,
                                        borderRadius: 2,
                                        background: 'rgba(255, 255, 255, 0.05)',
                                        display: 'flex',
                                        justifyContent: 'space-between',
                                        alignItems: 'center'
                                    }}
                                >
                                    <Box>
                                        <Typography variant="subtitle1">{exam.subject}</Typography>
                                        <Typography variant="body2" color="text.secondary">
                                            {exam.date} at {exam.time}
                                        </Typography>
                                    </Box>
                                    <CalendarTodayIcon sx={{ color: 'rgba(255, 255, 255, 0.3)' }} />
                                </Box>
                            ))}
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default Dashboard;
