import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  MenuItem,
  Chip,
  IconButton,
  Alert,
  CircularProgress,
  Snackbar,
  SnackbarContent,
  GlobalStyles
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';
import { motion } from 'framer-motion';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';

const API_URL = 'http://127.0.0.1:5000';

const priorities = [
  { value: 'high', label: 'High', color: '#DC143C' },
  { value: 'medium', label: 'Medium', color: '#FFA500' },
  { value: 'low', label: 'Low', color: '#4CAF50' }
];

function TaskScheduler() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    subject: '',
    priority: 'medium',
    estimatedTime: '',
    date: new Date()
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_URL}/tasks`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTasks(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch tasks');
    } finally {
      setLoading(false);
    }
  };

  // Update the formData reset in handleSubmit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${API_URL}/tasks`,
        formData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTasks([...tasks, response.data]);
      setSnackbarMessage('Task added successfully!');
      setSnackbarOpen(true);
      setFormData({
        title: '',
        description: '',
        subject: '',
        priority: 'medium',
        estimatedTime: '',
        due_date: null,  // Reset due_date to null
        date: new Date()
      });
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to add task');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${API_URL}/tasks/${taskId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setTasks(tasks.filter(task => task.id !== taskId));
      setSnackbarMessage('Task deleted successfully!');
      setSnackbarOpen(true);
    } catch (err) {
      setError('Failed to delete task');
    }
  };

  const textFieldStyle = {
    '& .MuiOutlinedInput-root': {
      backgroundColor: 'rgba(255, 255, 255, 0.03)',
      '& fieldset': {
        borderColor: 'rgba(255, 255, 255, 0.1)',
      },
      '&:hover fieldset': {
        borderColor: 'rgba(255, 255, 255, 0.2)',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#DC143C',
      }
    },
    '& .MuiInputLabel-root': {
      color: 'rgba(255, 255, 255, 0.7)',
    },
    '& .MuiInputBase-input': {
      color: '#fff',
    }
  };

  const sortedTasks = tasks.sort((a, b) => {
    const priorityOrder = {
      'high': 1,
      'medium': 2,
      'low': 3
    };

    if (priorityOrder[a.priority] !== priorityOrder[b.priority]) {
      return priorityOrder[a.priority] - priorityOrder[b.priority];
    }

    return new Date(a.date) - new Date(b.date);
  });

  return (
    <Box
      sx={{
        minHeight: '100vh',
        height: '100vh',
        overflow: 'hidden',
        background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        py: 4,
        px: 3,
      }}
    >
      <GlobalStyles
        styles={{
          'body': {
            margin: 0,
            overflow: 'hidden',
            position: 'fixed',
            width: '100%',
            height: '100%',
          },
          '*::-webkit-scrollbar': {
            width: '6px',
            background: 'transparent',
          },
          '*::-webkit-scrollbar-thumb': {
            background: 'rgba(255, 255, 255, 0.2)',
            borderRadius: '3px',
            '&:hover': {
              background: 'rgba(255, 255, 255, 0.3)',
            },
          },
          '*::-webkit-scrollbar-track': {
            background: 'transparent',
          },
        }}
      />

      <Container maxWidth="lg">
        <Grid container spacing={4} justifyContent="center">
          <Grid item xs={12} md={4}>
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
            >
              <Box sx={{ mb: 4 }}>
                <Grid container spacing={4}>
                  <Grid item xs={6}>
                    <Paper
                      sx={{
                        p: 2,
                        background: 'rgba(220, 20, 60, 0.1)',
                        borderRadius: 3,
                        border: '1px solid rgba(220, 20, 60, 0.2)',
                      }}
                    >
                      <Typography variant="h3" sx={{ color: '#DC143C', fontWeight: 600 }}>
                        {tasks.length}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        Total Tasks
                      </Typography>
                    </Paper>
                  </Grid>
                  <Grid item xs={6}>
                    <Paper
                      sx={{
                        p: 2,
                        background: 'rgba(255, 255, 255, 0.03)',
                        borderRadius: 3,
                        border: '1px solid rgba(255, 255, 255, 0.1)',
                      }}
                    >
                      <Typography variant="h3" sx={{ color: '#fff', fontWeight: 600 }}>
                        {tasks.filter(t => t.priority === 'high').length}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        High Priority
                      </Typography>
                    </Paper>
                  </Grid>
                </Grid>
              </Box>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <Paper
                elevation={0}
                sx={{
                  p: 3,
                  background: 'rgba(17, 17, 17, 0.6)',
                  backdropFilter: 'blur(20px)',
                  borderRadius: 4,
                  border: '1px solid rgba(255, 255, 255, 0.1)',
                  position: 'sticky',
                  top: 24,
                }}
              >
                <Typography variant="h5" sx={{ mb: 3, color: '#fff' }}>
                  Create New Task
                </Typography>

                {error && (
                  <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                  </Alert>
                )}

                <form onSubmit={handleSubmit}>
                  <Grid container spacing={3}>
                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        label="Task Title"
                        name="title"
                        value={formData.title}
                        onChange={(e) =>
                          setFormData({
                            ...formData,
                            title: e.target.value,
                          })
                        }
                        required
                        sx={{
                          ...textFieldStyle,
                          '& .MuiOutlinedInput-root': {
                            ...textFieldStyle['& .MuiOutlinedInput-root'],
                            borderRadius: '12px',
                            fontSize: '1.1rem',
                          }
                        }}
                      />
                    </Grid>

                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        multiline
                        rows={4}
                        label="Description"
                        name="description"
                        value={formData.description}
                        onChange={(e) =>
                          setFormData({
                            ...formData,
                            description: e.target.value,
                          })
                        }
                        sx={{
                          ...textFieldStyle,
                          '& .MuiOutlinedInput-root': {
                            ...textFieldStyle['& .MuiOutlinedInput-root'],
                            borderRadius: '12px',
                          }
                        }}
                      />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                      <TextField
                        fullWidth
                        select
                        label="Priority"
                        name="priority"
                        value={formData.priority}
                        onChange={(e) =>
                          setFormData({
                            ...formData,
                            priority: e.target.value,
                          })
                        }
                        sx={{
                          ...textFieldStyle,
                          '& .MuiOutlinedInput-root': {
                            ...textFieldStyle['& .MuiOutlinedInput-root'],
                            borderRadius: '12px',
                          }
                        }}
                      >
                        {priorities.map((priority) => (
                          <MenuItem key={priority.value} value={priority.value}>
                            <Box sx={{ display: 'flex', alignItems: 'center', py: 0.5 }}>
                              <Box
                                sx={{
                                  width: 14,
                                  height: 14,
                                  borderRadius: '50%',
                                  backgroundColor: priority.color,
                                  mr: 1.5,
                                }}
                              />
                              {priority.label}
                            </Box>
                          </MenuItem>
                        ))}
                      </TextField>
                    </Grid>

                    <Grid item xs={12} sm={6}>
                      <TextField
                        fullWidth
                        type="number"
                        label="Estimated Time (hours)"
                        name="estimatedTime"
                        value={formData.estimatedTime}
                        onChange={(e) =>
                          setFormData({
                            ...formData,
                            estimatedTime: e.target.value,
                          })
                        }
                        InputProps={{
                          inputProps: { min: 0, step: 0.5 },
                          startAdornment: (
                            <Box component="span" sx={{ color: 'rgba(255, 255, 255, 0.7)', mr: 1 }}>

                            </Box>
                          ),
                        }}
                        sx={{
                          ...textFieldStyle,
                          '& .MuiOutlinedInput-root': {
                            ...textFieldStyle['& .MuiOutlinedInput-root'],
                            borderRadius: '12px',
                          }
                        }}
                      />
                    </Grid>

                    <Grid item xs={12}>
                      <LocalizationProvider dateAdapter={AdapterDateFns}>
                        <DatePicker
                          label="Due Date"
                          value={formData.due_date}
                          onChange={(newValue) => {
                            setFormData({
                              ...formData,
                              due_date: newValue
                            });
                          }}
                          sx={{
                            width: '100%',
                            ...textFieldStyle,
                            '& .MuiOutlinedInput-root': {
                              ...textFieldStyle['& .MuiOutlinedInput-root'],
                              borderRadius: '12px',
                            },
                            '& .MuiInputBase-root': {
                              color: '#fff',
                            },
                            '& .MuiSvgIcon-root': {
                              color: 'rgba(255, 255, 255, 0.7)',
                            }
                          }}
                        />
                      </LocalizationProvider>
                    </Grid>

                    <Grid item xs={12}>
                      <Button
                        type="submit"
                        variant="contained"
                        fullWidth
                        disabled={loading}
                        sx={{
                          py: 2,
                          background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                          borderRadius: '12px',
                          textTransform: 'none',
                          fontSize: '1.2rem',
                          fontWeight: 600,
                          letterSpacing: '0.5px',
                          boxShadow: '0 8px 16px rgba(220, 20, 60, 0.2)',
                          '&:hover': {
                            background: 'linear-gradient(45deg, #FF4D6A, #DC143C)',
                            transform: 'translateY(-2px)',
                            boxShadow: '0 12px 20px rgba(220, 20, 60, 0.3)',
                          },
                          transition: 'all 0.3s ease',
                        }}
                      >
                        {loading ? (
                          <CircularProgress size={24} sx={{ color: '#fff' }} />
                        ) : (
                          <>
                            <Box component="span" sx={{ mr: 1 }}></Box>
                            Create Task
                          </>
                        )}
                      </Button>
                    </Grid>
                  </Grid>
                </form>
              </Paper>
            </motion.div>
          </Grid>

          <Grid item xs={12} md={8}>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              <Paper
                elevation={0}
                sx={{
                  p: 2.5,
                  background: 'rgba(17, 17, 17, 0.6)',
                  backdropFilter: 'blur(20px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.1)',
                  height: 'calc(75vh - 64px - 32px)',
                  overflowY: 'auto',
                  '&::-webkit-scrollbar': {
                    width: '6px',
                    background: 'transparent',
                  },
                  '&::-webkit-scrollbar-thumb': {
                    background: 'rgba(255, 255, 255, 0.2)',
                    borderRadius: '3px',
                    '&:hover': {
                      background: 'rgba(255, 255, 255, 0.3)',
                    },
                  },
                  '&::-webkit-scrollbar-track': {
                    background: 'transparent',
                  },
                }}
              >
                <Box
                  sx={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mb: 2,
                    px: 1,
                  }}
                >
                  <Typography variant="h6" sx={{ color: '#fff', fontWeight: 500 }}>
                    Your Tasks
                  </Typography>
                  <Chip
                    label={`${tasks.length} tasks`}
                    size="small"
                    sx={{
                      backgroundColor: 'rgba(255, 255, 255, 0.1)',
                      color: '#fff',
                      height: '24px',
                    }}
                  />
                </Box>

                {loading ? (
                  <Box sx={{ display: 'flex', justifyContent: 'center', py: 3 }}>
                    <CircularProgress size={24} />
                  </Box>
                ) : tasks.length === 0 ? (
                  <Box
                    sx={{
                      py: 3,
                      textAlign: 'center',
                      color: 'rgba(255, 255, 255, 0.5)',
                    }}
                  >
                    No tasks yet. Create your first task!
                  </Box>
                ) : (
                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    {sortedTasks.map((task) => (
                      <Paper
                        key={task.id}
                        sx={{
                          p: 2,
                          backgroundColor: 'rgba(255, 255, 255, 0.05)',
                          borderRadius: 3,
                          transition: 'background-color 0.3s',
                          '&:hover': {
                            backgroundColor: 'rgba(255, 255, 255, 0.1)',
                          },
                        }}
                      >
                        <Box
                          sx={{
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                          }}
                        >
                          <Box sx={{ flex: 1 }}>
                            <Typography
                              sx={{
                                color: '#fff',
                                fontSize: '1.1rem',
                                fontWeight: 600,
                                mb: 0.5,
                              }}
                            >
                              {task.title}
                            </Typography>
                            {task.subject && (
                              <Typography
                                variant="body2"
                                sx={{
                                  color: 'rgba(255, 255, 255, 0.6)',
                                  fontSize: '0.9rem',
                                  mb: 0.5,
                                }}
                              >
                                {task.subject}
                              </Typography>
                            )}
                            {task.description && (
                              <Typography
                                variant="body2"
                                sx={{
                                  color: 'rgba(255, 255, 255, 0.5)',
                                  fontSize: '0.9rem',
                                }}
                              >
                                {task.description}
                              </Typography>
                            )}
                            <Typography
                              variant="body2"
                              sx={{
                                color: 'rgba(255, 255, 255, 0.4)',
                                fontSize: '0.8rem',
                                mt: 0.5,
                              }}
                            >
                               {task.due_date ? new Date(task.due_date).toLocaleDateString() : 'No due date'} 
                              &nbsp;  {task.estimated_time ? `${task.estimated_time}h` : 'No estimate'}
                            </Typography>
                          </Box>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <Chip
                              label={task.priority}
                              size="small"
                              sx={{
                                backgroundColor: `${priorities.find((p) => p.value === task.priority)?.color}15`,
                                color: priorities.find((p) => p.value === task.priority)?.color,
                                fontSize: '0.8rem',
                                fontWeight: 500,
                                textTransform: 'capitalize',
                              }}
                            />
                            <IconButton
                              size="small"
                              onClick={() => handleDelete(task.id)}
                              sx={{
                                color: 'rgba(255, 255, 255, 0.3)',
                                '&:hover': {
                                  color: '#DC143C',
                                  backgroundColor: 'rgba(220, 20, 60, 0.1)',
                                },
                              }}
                            >
                              <DeleteIcon sx={{ fontSize: '1.1rem' }} />
                            </IconButton>
                          </Box>
                        </Box>
                      </Paper>
                    ))}
                  </Box>
                )}
              </Paper>
            </motion.div>
          </Grid>
        </Grid>
      </Container>

      {/* Snackbar for success messages */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={3000}
        onClose={() => setSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <SnackbarContent
          message={snackbarMessage}
          sx={{
            backgroundColor: '#4CAF50', // Green color
            color: '#fff', // Text color
          }}
        />
      </Snackbar>
    </Box>
  );
}

