import React, { useState, useEffect } from 'react';
import {
    Box,
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Chip,
    IconButton,
    Alert,
    CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
import axios from 'axios';

const API_URL = 'http://127.0.0.1:5000';

const priorities = [
    { value: 'high', label: 'High', color: '#DC143C' },
    { value: 'medium', label: 'Medium', color: '#FFA500' },
    { value: 'low', label: 'Low', color: '#4CAF50' }
];

function TaskScheduler() {
    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        subject: '',
        priority: 'medium',
        estimatedTime: ''
    });

    // Fetch tasks on component mount
    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            console.log('Using token:', token); // Debug log

            const response = await axios.get(`${API_URL}/tasks`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            console.log('Response:', response.data); // Debug log
            setTasks(response.data);
        } catch (err) {
            console.error('Full error:', err); // More detailed error logging
            setError(err.response?.data?.error || 'Failed to fetch tasks');
        } finally {
            setLoading(false);
        }
    };    

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setLoading(true);

        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_URL}/tasks`,
                formData,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                }
            );

            setTasks([...tasks, response.data]);
            setSuccess('Task added successfully!');
            setFormData({
                title: '',
                description: '',
                subject: '',
                priority: 'medium',
                estimatedTime: ''
            });
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to add task');
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (taskId) => {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`${API_URL}/tasks/${taskId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setTasks(tasks.filter(task => task.id !== taskId));
            setSuccess('Task deleted successfully!');
        } catch (err) {
            setError('Failed to delete task');
        }
    };

    return (
        <Box
            sx={{
                minHeight: 'calc(100vh - 64px)',
                background: 'linear-gradient(135deg, #0A0A0F 0%, #1a1a1f 100%)',
                py: 4,
                px: 3
            }}
        >
            <Container maxWidth="lg">
                <Grid container spacing={4}>
                    {/* Task Creation Form */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={24}
                            sx={{
                                p: 4,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                position: 'sticky',
                                top: 24
                            }}
                        >
                            <Typography 
                                variant="h5" 
                                sx={{ 
                                    mb: 4, 
                                    color: '#fff',
                                    fontWeight: 500,
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: 2,
                                    '&::before': {
                                        content: '""',
                                        width: 4,
                                        height: 24,
                                        background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                                        borderRadius: 2
                                    }
                                }}
                            >
                                Create New Task
                            </Typography>

                            {error && (
                                <Alert 
                                    severity="error" 
                                    sx={{ 
                                        mb: 3,
                                        backgroundColor: 'rgba(211, 47, 47, 0.1)',
                                        color: '#ff8a80'
                                    }}
                                >
                                    {error}
                                </Alert>
                            )}

                            {success && (
                                <Alert 
                                    severity="success" 
                                    sx={{ 
                                        mb: 3,
                                        backgroundColor: 'rgba(76, 175, 80, 0.1)',
                                        color: '#69f0ae'
                                    }}
                                >
                                    {success}
                                </Alert>
                            )}

                            <form onSubmit={handleSubmit}>
                                <Grid container spacing={3}>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Task Title"
                                            name="title"
                                            value={formData.title}
                                            onChange={(e) => setFormData({
                                                ...formData,
                                                title: e.target.value
                                            })}
                                            required
                                            sx={{
                                                '& .MuiOutlinedInput-root': {
                                                    backgroundColor: 'rgba(255, 255, 255, 0.03)',
                                                    '& fieldset': {
                                                        borderColor: 'rgba(255, 255, 255, 0.1)',
                                                    },
                                                    '&:hover fieldset': {
                                                        borderColor: 'rgba(255, 255, 255, 0.2)',
                                                    },
                                                    '&.Mui-focused fieldset': {
                                                        borderColor: '#DC143C',
                                                    }
                                                },
                                                '& .MuiInputLabel-root': {
                                                    color: 'rgba(255, 255, 255, 0.7)',
                                                },
                                                '& .MuiInputBase-input': {
                                                    color: '#fff',
                                                }
                                            }}
                                        />
                                    </Grid>

                                    {/* Apply similar TextField styling to other form fields */}

                                    <Grid item xs={12}>
                                        <Button
                                            type="submit"
                                            variant="contained"
                                            fullWidth
                                            disabled={loading}
                                            sx={{
                                                py: 1.5,
                                                background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                                                borderRadius: 2,
                                                textTransform: 'none',
                                                fontSize: '1.1rem',
                                                fontWeight: 500,
                                                boxShadow: '0 8px 16px rgba(220, 20, 60, 0.2)',
                                                '&:hover': {
                                                    background: 'linear-gradient(45deg, #FF4D6A, #DC143C)',
                                                    transform: 'translateY(-2px)',
                                                    boxShadow: '0 12px 20px rgba(220, 20, 60, 0.3)',
                                                },
                                                transition: 'all 0.3s ease'
                                            }}
                                        >
                                            {loading ? <CircularProgress size={24} /> : 'Create Task'}
                                        </Button>
                                    </Grid>
                                </Grid>
                            </form>
                        </Paper>
                    </Grid>

                    {/* Task List */}
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={24}
                            sx={{
                                p: 4,
                                background: 'rgba(17, 17, 17, 0.6)',
                                backdropFilter: 'blur(20px)',
                                borderRadius: 4,
                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                minHeight: 600
                            }}
                        >
                            <Box sx={{ 
                                display: 'flex', 
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                mb: 4
                            }}>
                                <Typography 
                                    variant="h5" 
                                    sx={{ 
                                        color: '#fff',
                                        fontWeight: 500,
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: 2,
                                        '&::before': {
                                            content: '""',
                                            width: 4,
                                            height: 24,
                                            background: 'linear-gradient(45deg, #DC143C, #FF4D6A)',
                                            borderRadius: 2
                                        }
                                    }}
                                >
                                    Your Tasks
                                </Typography>
                                <Chip 
                                    label={`${tasks.length} tasks`} 
                                    size="small"
                                    sx={{
                                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                                        color: '#fff',
                                        border: '1px solid rgba(255, 255, 255, 0.1)'
                                    }}
                                />
                            </Box>

                            {loading ? (
                                <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
                                    <CircularProgress sx={{ color: '#DC143C' }} />
                                </Box>
                            ) : tasks.length === 0 ? (
                                <Box
                                    sx={{
                                        textAlign: 'center',
                                        py: 8,
                                        color: 'rgba(255, 255, 255, 0.5)'
                                    }}
                                >
                                    <Typography variant="h6" gutterBottom>
                                        No tasks yet
                                    </Typography>
                                    <Typography variant="body2">
                                        Create your first task to get started
                                    </Typography>
                                </Box>
                            ) : (
                                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                                    {tasks.map((task) => (
                                        <Paper
                                            key={task.id}
                                            sx={{
                                                p: 3,
                                                background: 'rgba(255, 255, 255, 0.03)',
                                                borderRadius: 3,
                                                transition: 'all 0.3s ease',
                                                border: '1px solid rgba(255, 255, 255, 0.1)',
                                                '&:hover': {
                                                    transform: 'translateY(-2px)',
                                                    background: 'rgba(255, 255, 255, 0.05)',
                                                    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)'
                                                }
                                            }}
                                        >
                                            {/* Keep the same task card content structure with enhanced styling */}
                                            {/* ... */}
                                        </Paper>
                                    ))}
                                </Box>
                            )}
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default TaskScheduler;
