import React, { useState } from 'react';
import { 
    Box, 
    Container, 
    Grid,
    Typography,
    IconButton,
    Tooltip,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Button,
    TextField,
    MenuItem,
    Fade,
    Paper
} from '@mui/material';
import { motion } from 'framer-motion';
import { useSpring, animated } from '@react-spring/web';
import { useInView } from 'react-intersection-observer';

// Icons
import AddIcon from '@mui/icons-material/Add';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import SchoolIcon from '@mui/icons-material/School';
import AssignmentIcon from '@mui/icons-material/Assignment';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import CloseIcon from '@mui/icons-material/Close';


// Animation variants
const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
};

const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: {
            type: "spring",
            stiffness: 100
        }
    }
};

// Custom animated card component with micro-interactions
const DashboardCard = ({ 
    icon: Icon, 
    title, 
    description, 
    onClick, 
    color = '#DC143C',
    onAddClick 
}) => {
    const [isHovered, setIsHovered] = useState(false);
    const [ref, inView] = useInView({
        threshold: 0.2,
        triggerOnce: true
    });

    const springProps = useSpring({
        transform: isHovered ? 'scale(1.02)' : 'scale(1)',
        boxShadow: isHovered 
            ? '0 10px 30px rgba(220, 20, 60, 0.2)' 
            : '0 4px 20px rgba(0, 0, 0, 0.1)',
        config: { tension: 300, friction: 20 }
    });

    return (
        <motion.div
            ref={ref}
            initial="hidden"
            animate={inView ? "visible" : "hidden"}
            variants={itemVariants}
            style={{ height: '100%' }}
        >
            <animated.div
                style={{
                    ...springProps,
                    height: '100%',
                    background: 'rgba(17, 17, 17, 0.6)',
                    backdropFilter: 'blur(20px)',
                    borderRadius: '24px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    overflow: 'hidden',
                    position: 'relative'
                }}
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
            >
                <Box
                    sx={{
                        p: 3,
                        height: '100%',
                        display: 'flex',
                        flexDirection: 'column',
                        position: 'relative',
                        zIndex: 1,
                    }}
                >
                    <Box
                        sx={{
                            display: 'flex',
                            alignItems: 'center',
                            mb: 2
                        }}
                    >
                        <Box
                            sx={{
                                width: 48,
                                height: 48,
                                borderRadius: '14px',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                background: `${color}15`,
                                mr: 2,
                                transition: 'all 0.3s ease',
                                transform: isHovered ? 'scale(1.1)' : 'scale(1)'
                            }}
                        >
                            <Icon sx={{ color: color, fontSize: 24 }} />
                        </Box>
                        <Typography
                            variant="h6"
                            sx={{
                                fontWeight: 600,
                                color: 'rgba(255, 255, 255, 0.9)',
                                flex: 1
                            }}
                        >
                            {title}
                        </Typography>
                        <IconButton
                            sx={{
                                color: color,
                                opacity: isHovered ? 1 : 0.5,
                                transform: isHovered ? 'translateX(4px)' : 'translateX(0)',
                                transition: 'all 0.3s ease'
                            }}
                            onClick={onClick}
                        >
                            <ArrowForwardIcon />
                        </IconButton>
                    </Box>
                    <Typography
                        variant="body2"
                        sx={{
                            color: 'rgba(255, 255, 255, 0.6)',
                            mb: 2
                        }}
                    >
                        {description}
                    </Typography>
                    <Box
                        sx={{
                            mt: 'auto',
                            display: 'flex',
                            justifyContent: 'flex-end'
                        }}
                    >
                        <Tooltip title={`Add ${title}`}>
                            <IconButton
                                onClick={onAddClick}
                                sx={{
                                    background: isHovered ? `${color}20` : 'transparent',
                                    color: color,
                                    transition: 'all 0.3s ease',
                                    '&:hover': {
                                        background: `${color}30`,
                                        transform: 'rotate(90deg)'
                                    }
                                }}
                            >
                                <AddIcon />
                            </IconButton>
                        </Tooltip>
                    </Box>
                </Box>
                <Box
                    sx={{
                        position: 'absolute',
                        top: isHovered ? '-50%' : '-150%',
                        right: '-50%',
                        width: '200%',
                        height: '200%',
                        background: `radial-gradient(circle, ${color}10 0%, transparent 70%)`,
                        transition: 'all 0.5s ease',
                        opacity: isHovered ? 1 : 0,
                        pointerEvents: 'none'
                    }}
                />
            </animated.div>
        </motion.div>
    );
};

// Custom Dialog component with animations
const CustomDialog = ({ open, onClose, title, children }) => {
    return (
        <Dialog
            open={open}
            onClose={onClose}
            maxWidth="sm"
            fullWidth
            TransitionComponent={Fade}
            TransitionProps={{ timeout: 400 }}
            PaperProps={{
                sx: {
                    background: 'rgba(17, 17, 17, 0.95)',
                    backdropFilter: 'blur(20px)',
                    borderRadius: '24px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                }
            }}
        >
            <DialogTitle sx={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'space-between',
                borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
            }}>
                <Typography variant="h6" sx={{ color: 'rgba(255, 255, 255, 0.9)' }}>
                    {title}
                </Typography>
                <IconButton onClick={onClose} sx={{ color: 'rgba(255, 255, 255, 0.5)' }}>
                    <CloseIcon />
                </IconButton>
            </DialogTitle>
            <DialogContent sx={{ mt: 2 }}>
                {children}
            </DialogContent>
        </Dialog>
    );
};

// Main Dashboard Component
function Dashboard() {
    // Dialog states
    const [openDialog, setOpenDialog] = useState('');
    const [formData, setFormData] = useState({});

    // Example dialog handlers - To be replaced with actual functionality
    const handleOpenDialog = (dialogType) => {
        setOpenDialog(dialogType);
        setFormData({}); // Reset form data
    };

    const handleCloseDialog = () => {
        setOpenDialog('');
        setFormData({});
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        // TODO: Implement form submission logic based on dialog type
        console.log('Form submitted:', openDialog, formData);
        handleCloseDialog();
    };

    // Card configurations
    const cardConfigs = [
        {
            id: 'classes',
            icon: SchoolIcon,
            title: 'Class Schedule',
            description: 'Manage your class schedule and get organized',
            color: '#DC143C'
        },
        {
            id: 'exams',
            icon: CalendarTodayIcon,
            title: 'Exam Dates',
            description: 'Track your upcoming exams and prepare effectively',
            color: '#4CAF50'
